**/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("onelook.com") {


/* Main Logo Swap */

img[src="/img/img_ol-logo_hp_264x84px.png"] {

height: 0 !important;
width: 0 !important;
padding-right:291px !important;
padding-top: 53px !important;
background: url("data:image/png;base64,") no-repeat;
}

/* Small logo swap */

img[src="/img/ol5st.GIF"] {
height: 0 !important;
width: 0 !important;
padding-right:129px !important;
padding-top: 23px !important;
background: url("data:image/png;base64,") no-repeat;
}


/* === general color change === */

/* remove border colors */
* {
border-color: transparent !important;
}

/* basic bodies */
html {

background: #2E2F37 -moz-linear-gradient(left, #222, #2E2F37, #222) !important;
background: #2E2F37 -webkit-linear-gradient(left, #222, #2E2F37, #222) !important;
}

body {
background: transparent !important; 
color: #ddd !important;
margin-bottom: 1em !important;
}

font[color] {
color: inherit !important;
}

/* links */

a:link  {
color: #7AC9C9 !important;
}
a:visited {
color: #BAA4D6 !important;
}
a:hover {
color: #FDFF55 !important;
}

/* text input */

input[type="text"] { 
-moz-appearance: none !important;
padding: 0 1em !important;
color: #EEE !important;
background: #444 !important;
border: 2px solid #111 !important;
max-width: 32em !important;
line-height: 2 !important
}

textarea, input[name="email"]{
-moz-appearance: none !important;
border: 2px solid #111 !important; 
background: #444 !important; 
color: #eee !important;
}

/* submit button */

input[type="submit"]{
-moz-appearance: none !important;
color: #EEE !important;
background: #4F505E !important;
border-radius: 4px;
border: 2px solid #111 !important;
line-height: 2 !important;
}

input[type="submit"]::-moz-focus-inner {
border: none !important;
}

input[type="submit"]:hover {
background: #435B6B !important; color: #FFF !important;
}
input[type="submit"]:focus {
border-color: #418CE8 !important;
}
input[type="submit"]:active {
background: #000 !important;
}

/* bg color for elements with [bgcolor] */
body [bgcolor] {
background-color: rgba(10,10,10,.25) !important; 
border: 2px solid rgba(0,0,0,.3) !important
}

/* remove styling of [bgcolor] within [bgcolor] */
body [bgcolor] [bgcolor] {
background: transparent !important; border: none !important;
}

/* === Formatting change === */

/* body {

width:95% !important;
font-size: 1em !important;
max-width: 1200px !important;
margin-left: auto !important;
margin-right: auto !important;
}  */

/* Jump to: ... (center text) */
td[align="left"][valign="top"] + td + td P[align="right"] {
text-align: center !important;
}

/* === Specific color change === */

/* Header, Footer */
html > body > table[width="100%"][cellspacing="0"][border="0"][bgcolor] {
background: none rgba(0,0,0,.4) !important;
}

html > body > table[width="100%"][cellspacing="0"][border="0"][bgcolor] a {color: #A7B9C4 !important;}
html > body > table[width="100%"][cellspacing="0"][border="0"][bgcolor] a:hover {
color: #FDFF55 !important;
}

/* Jump to dictionary headings, query emphasis, grammar type */
a + u > b,
#orth,
#easel_hw,
div.hom > div.gramGrp > div.gram[type="pos"],
[id^="easel_pos"] {
color: #F8F8F8 !important;
}

/* Word usage examples*/
[id^="easel_exs"] {
color: #40C97E !important;
}

/* autocomplete */
.autocomplete:empty {
display: none !important;
}

/* seems harmless */
html > body > center:nth-child(3) + center {
position: relative !important;
}

#autocomplete_choices {
background: rgba(0,0,0,.95) !important
}

.selected {
background: rgba(80,80,120,.4) !important;
}

.informal {
color: #F8F8F8 !important;
}

/* Macmillan Speaker Icon */
#mm_audio_logo {

opacity: .9;
width: 0 !important;
height: 0 !important;
padding-left: 39px !important;
padding-top: 39px !important;
background: transparent url("data:image/png;base64,") !important;
}

#mm_audio_logo:hover {
opacity: 1 !important;
}


#mm_logo {
width: 0 !important;
height: 0 !important;
padding-left: 165px !important;
padding-top: 40px !important;
background: transparent url("data:image/png;base64,") !important;
}


/* Hide */

img[src="/img/img_ol-logo_sm_345x42px.png"],
img[src="/img/news1.jpg"],
a[href*="http://a.tribalfusion.com/"],
iframe, embed,
#___plusone_0,
td[align="left"][valign="top"] > table[width][cellpadding="0"][border="0"][bgcolor="#ffffff"] ~ table:last-of-type {
display: none !important;
}

}
