css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twoo.com") {

div.bgw.brgm.b1.socialbuttons {
    display: none !important;
}

#wrapper {
    background-color: #073954 !important;
}

#footer {
    background-color: #073954 !important;
    display: none !important;
}

body.ltr.hu.loggedIn {
    background-color: #073954 !important;
}

div.clearfix.p10 {
    background-color: #004869 !important;
}

div.box.box20.clearfix {
    background-color: #004869 !important;
}

/*
fieldset.rad5.mb10 {
    background-color: #004480 !important;
}
*/

/*
fieldset.rad5.requests {
    background-color: #004480 !important;
}
*/

legend.fwb.ph5 {
    color: #ffffff !important;
}

legend.fwb {
    color: #ffffff !important;
}

.buttonDiamond {
    display: none !important;
}

.userInfo {
    background-color: transparent !important;
}

.userPhotos {
    background-color: transparent !important;
}

.box {
    border-color: #6592C5 !important;
    background-color: #004869 !important;
    -webkit-box-shadow: none !important;
}

.sidebar {
    background: none !important;
    border: 1px solid #6592C5 !important;
    background-color: #004869 !important;
}

body {
    color: white !important;
}

.sidebar ul li.item {
    background-color: #0077AD !important;
    border-color: #6592C5 !important;
}

.sidebar ul li.item > a {
    color: white !important;
    text-shadow: none !important;
}

a {
    color: #AAA !important;
}

.sidebar ul li.item span.count {
    color: white !important;
}

.sidebar ul li.item.last {
}

input.text, textarea.textarea {
    background: #0077AD !important;
    border-color: #6592C5 !important;
    color: white !important;
}

.fcgd {
    color: #AAA !important;
}

li.photoBlock a > img {
    border-color: #6592C5 !important;
}

.sidebar #onlineRandomPeople {
    border-color: #6592C5 !important;
}

.actionState {
    background-color: #004869 !important;
    color: white !important;
    border: 1px solid #6592C5 !important;
}

.actionState a {
    color: orange !important;
}

#photoHolder img {
    border: 1px solid #6592C5 !important;
}

#headerUserInfo .avatar {
    border-color: #6592C5 !important;
}

div.simpleFilter {
    background: none !important;
}

.bgw {
    background-color: #004869 !important;
    border-color: #6592C5 !important;
}

.bgw a {
    color: orange !important;
}

.messagesHeader .background {
    background: none !important;
}

.messagesHeader {
    background: none !important;
    background-color: #0077AD !important;
}

.messageList .conversation {
    background-color: #004869 !important;
    border-bottom: 1px solid #6592C5 !important;
}

.messageList {
    border: 1px solid #6592C5 !important;
}

div.requests fieldset {
    background-color: #004869 !important;
}

fieldset.rad5.mb10.requests {
    display: none !important;
}

.logo img {
    display: none !important;
}

.logo {
    width: 179px !important;
    height: 48px !important;
    background: url("https://sites.google.com/site/fikusz20120222/home/logo-twoo-tp.gif") top center no-repeat !important;
    float: left !important;
}

.boxOverview .boxOverviewInner {
    background-color: #004869 !important;
    border: 1px solid #6592C5 !important;
}

.boxOverview .boxOverviewInner > div {
    background-color: #004869 !important;
}

li.onlinematch.selected {
    background: #0077AD !important;
}

li.onlinematch img.avatar {
    border-color: #6592C5 !important;
}

.boxOverviewBottom fieldset {
    border-color: #6592C5 !important;
}

/*
.bo {
    background-color: #004869 !important;
}
*/

.balloon {
    border-color: #6592C5 !important;
    color: white !important;
}

.balloon.me {
    background-color: #073954 !important;
}

.balloon.owner {
    background-color: #0077AD !important;
}

/*
.balloon.me .cssArrow {
}
*/

.messageDetail {
    background-color: transparent !important;
}

.newMessageForm {
    background-color: #004869 !important;
}

div.threadWrapperNoPopup {
    border: 0 !important;
}

#otherPerson {
    border: 0 !important;
}

.button {
    background: transparent !important;
    background-color: #0077AD !important;
    text-shadow: none !important;
    border-color: #6592C5 !important;
    color: orange !important;
}

.buttonGrey {
    background: transparent !important;
    background-color: #0077AD !important;
    text-shadow: none !important;
}

.boxGame #gameFormInside {
    background-image: none !important;
    background-color: #035987 !important;
    border: 1px solid #6592C5 !important;
}

a.yes {
    color: lightgreen !important;
}

a.maybe {
    color: lightgray !important;
}

a.no {
    color: #F17373 !important;
}

div.messagebox {
    text-shadow: none !important;
}

#formFilter {
    background: #073954 none !important;
}

.advancedSearch .searchBox {
    background: transparent !important;
    background-color: #004869 !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
}

fieldset {
    border: 1px solid #6592C5 !important;
}

.sidebar ul li .sidebarMatches {
    background: transparent !important;
    background-color: #0077AD !important;
    border: 1px solid #6592C5 !important;
}

.sidebar ul li ul.switches {
    background: transparent !important;
    background-color: #0077AD !important;
    border: 1px solid #6592C5 !important;
    text-shadow: none !important;
    color: white !important;
}

.profileBlockContainer {
    background: #073954 none !important;
}

.profileBlock {
    background: #004869 !important;
    margin-bottom: 3px !important;
    border: solid 1px #6592C5 !important;
}

.profileBlock a {
    color: orange !important;
}

.profileBlock .edit {
    background: #0077AD !important;
}

.blackTrans {
    -moz-opacity: 1 !important;
    -khtml-opacity: 1 !important;
    opacity: 1 !important;
}

fieldset.multipleIntField {
    background: #004869 !important;
}

ul.grid li {
    background-color: #073954 !important;
    text-shadow: none !important;
}

.promoBox.promoBoxOrange, .promoBox.promoBoxPurple {
    background: transparent !important;
    background-color: #0077AD !important;
}

.promoBox {
    border: solid 1px #6592C5 !important;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

.promoBox:hover p, .promoBox p {
    color: white !important;
    text-shadow: none !important;
}

.buttonFlatGrey, .buttonMatched, .buttonUnlike, .buttonLiked {
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

div.locationPromote {
    background: #004869 none !important;
    border: solid 1px #6592C5 !important;
    color: black !important;
}

h1, h2, h3, h4, h5, h6 {
    color: #AAA !important;
}

span.counterSmall, span.counter {
    background-color: #0077AD !important;
}

.bgor {
    background-color: #073954 !important;
}

a.slideBtn {
    background-color: #0077AD !important;
}

#profile .avatar {
    background-color: #0077AD !important;
}

ul.pictures li a {
    border-color: #6592C5 !important;
    padding: 0px !important;
}

.selected a {
    color: white !important;
}

.sidebar ul li.selected > a, .sidebar ul li.selected > a span {
    background: 0 !important;
    background-color: #AAA !important;
}

.gameRight {
    background: 0 !important;
    background-color: #073954 !important;
    border-color: #0077AD !important;
}



}
