css:
div.folderview .tt-a span.gallections, div.stream .tt-a span.gallections {
display:none;
}

.mcb-favecount, .mcb-commentcount, .mcb-commentcount a, div.stream div.tt-a div.commentcount, div.stream div.tt-a div.commentcount a, .commentcount {
font-size: 10px !important;
display: block;
width: 65px !important;
height: 14px !important;
overflow: hidden;
margin: auto;
}

#depths .pollbox .commentcount {
background-position-x: 3px;
background-position-y: 4px;
text-align: left;
display: inline-block;
}

#depths #footer-da-journal li .comments {
width: 30px !important;
overflow: hidden;
background-position-x: 4px;
background-position-y: 4px;
}

.journal-footer a.comments {
font-size: 11px !important;
width: 65px !important;
height: 14px !important;
overflow: hidden;
padding-bottom: 5px;
}

.journal-footer a.comments em {
background-position-y: 2px;
}

.journal-footer a.faves {
font-size: 11px !important;
width: 65px !important;
height: 15px !important;
overflow: hidden;
padding-bottom: 5px;
}

#journalsBrowseWrapper #sidebarPoll .commentcount {
text-align: left;
}
