@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://dev/java/jdk/docs/api/"), url-prefix("http://docs.oracle.com/javase/7/docs/api/") {

body, tt, code, pre, h1, h2, h3, h4, dt {
  font-size: inherit !important;
  color: inherit !important;
}

body, frame body, .blockList {
  font-family: inherit !important;
  background: #ffffed !important;
}

.indexContainer {
  font-size: 90% !important;
  margin: 1ex !important;
}
.indexContainer h2 {
  padding: 0px !important;
  margin: 0.75ex;
}

.indexHeader, dd {
  margin: 0.25ex 1ex 0ex 1ex !important;
}

.subTitle {
  color: green;
}

caption span {
  padding-top: inherit !important;
}

.topNav, .bottomNav {
  height: inherit !important;
}

.subNav {
  font-size: 87% !important;
  line-height: 87%;
}

.bar {
  padding: 0.5ex !important;
}

li.blockList pre {
  color: green !important;
}

p.legalCopy small font {
  font-size: 87% !important;
}

}
