css:
@-moz-document domain(startpage.com),
               domain(ixquick.com) {
  .air, html { 
    background-image:none!important; }
  
  #search #detail, /*enchanced by Google: details link*/
  #head #light, /* theme changer */
  #results_content .date_promo,  /* NEW! box in sidebar about dates */  
  #results_content #inner_promo_box,  /* NEW! box */   
  #results_content #tip,  /* Startpage does NOT record your IP address !  */             
  #logo.f-l img  /* start page logo on search results */
  { display:none!important;}  
  
  #footer_links     /* ixquick language options */
  { opacity:0!important;}   
             

  #results_content #side_bar {  
    width: 130px!important;  }
  #results_content #results {  
    margin-left: 130px!important;  }
  #results_content #results.no_side_bar {  
    margin-left: 0px!important;  }  
  #results_content #side_bar > UL {  
    border: 0px!important;  }   
    
  .air #content #search_form input#query,
  input#query_top,
  input#submit1,
  input#query_bottom {             
    border-radius: 0px!important;
    box-shadow: none!important;
    border: 1px inset black!important;
    font-size: medium!important;
  }
  
  #logo.f-l 
  { background-image: url("chrome://branding/content/icon64.png")!important;
     background-repeat: no-repeat!important;
     width:64px!important;
     height:64px!important;
  } 
  
  
  .air div#content {
  width:550px!important;
  }
  .air #content #main_link {
   margin: 0px!important;
   width:580px!important;
  }
         
  .air #content #main_link p:not([id="caption"]) {
   border: 1px solid #476C89;
   min-height: 60px!important;
   background-image: url("chrome://branding/content/about-wordmark.png"), -moz-linear-gradient(top, 
    #94A6B4, #476C89) !important;
   background-repeat: no-repeat!important;
   background-position: 20px 8px, top center!important;
   border-radius: 6px!important;
   margin: 0px!important;
   margin-right:30px!important;
   width:550px!important;
  } 
  
  .air #content #search_form #enhanced_cont #detail, /*enchanced by Google: details link*/
  .air #content ~ #footer,
  .air #content ~ #links,
  .air #content #main_link p img,
  .air #content #main_link p#caption > * { 
    display: none!important;}
  
  .air #content #main_link p#caption {
  min-height: 139px!important;
  background-image: url("chrome://branding/content/about-logo.png")!important;
  background-repeat: no-repeat!important;
  background-position: top right!important;
  color: transparent!important;
  background-size: contain; 
  margin-top: -90px!important; 
  width:580px!important;
  z-index: 10!important;
  position:relative!important;
  }

   .air #content #search_form {
   border: 1px solid #ABABAB!important;
   border-radius: 6px!important;
   padding-left: 5px!important;
   /*padding-right: 0px!important;*/
   padding-top: 70px!important;
   padding-bottom: 30px!important;
   margin-right: 30px!important;
   margin-top: -60px!important;
   width:550px!important;
   }

   .air #content #search_form #enhanced_cont {
   margin-top: -35px!important;
   margin-left: 35px!important;
   float: left!important;
   }
   .air #content #search_form input#query {  
    border-radius: 0px!important;
    box-shadow: none!important;           
    padding-left: 5px!important;
    margin-left: 30px!important;
   }
   
   /* apply only to startpage */
   .air #content #search_form #enhanced_cont ~ p input#query {
    margin-left: 0px!important;
   }
      
   .air #content #search_form input#query {  
    border-radius: 0px!important;
    box-shadow: none!important;           
    padding-left: 5px!important;
   }
   .air #content #search_form input#submit { 
     border-radius: 0px!important;
     box-shadow: none!important; 
   }              
           
}
