css:
/* FACEBOOK Hello Pinky Style by Scorpion Graphism */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") 
{

#contentCol  { 
    background-color:#fff3f6 !important; 
    background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/bckgrnd.jpg");
}
.profile .profile_color_bar  { background-color:#000000 !important; }

*{
    font-family : Comic Sans MS !important;
    color : purple !important;
}

/* navigation */
#blueBar        { background:#e78cd9 !important; }
#pageLogo a     { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/facebook.png") no-repeat center center !important; }
#headNavOut     { background:#e78cd9 !important; border-color:#e78cd9 !important; }

/* main nav icons */
#fbRequestsJewel .jewelButton, #fbRequestsJewel.hasNew .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/friends.png") no-repeat center center !important; }
#fbRequestsJewel:hover .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/friends-hover.png") no-repeat center center !important; }
#fbMessagesJewel .jewelButton, #fbMessagesJewel.hasNew .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/mail.png") no-repeat center center !important; }
#fbMessagesJewel:hover .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/mail-hover.png") no-repeat center center !important; }
#fbNotificationsJewel .jewelButton, #fbNotificationsJewel.hasNew .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/earth.png") no-repeat center center !important; }
#fbNotificationsJewel:hover .jewelButton { background: url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/earth-hover.png") no-repeat center center !important; }

/* search */
#navSearch button  { background:url("http://emilysdiary.freeiz.com/FBHelloPinkyStyle/imag/search.png") no-repeat center center !important; }
#navSearch     { border-color:#e78cd9 !important; }

/* nav tabs */
#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#e84fdd !important; color:#ffffff !important; }

/* boxes */
.lightblue_box  { background-color:#fce1e8 !important; border-color:#f6b1c3 !important; }
.commentable_item .ufi_section  { background-color:#fce1e8 !important; }
textarea, .inputtext, .inputpassword  { border-color:#f6b1c3 !important; }

/* links */
a  { color:#de264c !important; }
.uiLinkButton input  { color:#2c2c2c !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }
button.as_link  { color:#2c2c2c !important; }

/* side nav */
.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#f6b1c3 !important; }
.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#fce1e8 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#fce1e8 !important; }
.unread  { background-color:#f6b1c3 !important; }
.line  { color:#de264c !important; }

/* pop up box  */
#jewelCase .jewelFooter a   { background-color:#fce1e8 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#de264c !important;  }
#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#de264c !important; }
#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#fce1e8 !important; border-color:#f0788c !important; }
#jewelCase .jewelBox  { background-color:#fff3f6 !important; }

/* copy */
.uiHeader h2  { color:#de264c !important; }

/* graffiti */
.current_stuff td  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }

/* profile page */
.UIComposer .UIComposer_Box  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }
.profile .box .box_header  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }

/* profile tabs */
.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#f6b1c3 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#f6b0c2 !important; border-color:#f0788c !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#de264c !important; color:#ffffff !important; border-color:#de264c !important; }

/* chat */
#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#fff3f6 !important;  }
#chat_tab_bar .chat_header  { background-color:#f0788c !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#f6b1c3 !important; color:#de264c !important; }
#presence_bar .presence_menu_opts h2 span  { background:#f0788c !important; }
#presence_bar .presence_menu_opts h2  { border-color:#f0788c !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#fff3f6 !important; }
#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#f0788c !important; border-color:#f0788c !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }
#presence .presence_menu_opts  { border-color:#f0788c !important; }

/* buttons */
.UIButton_Blue  { background:#de264c !important; border-color:#f0788c !important; }
.UIComposer_InputArea  { border-color:#f6b1c3 !important; }
.uiButtonConfirm  { background:#de264c !important; border-color:#f0788c !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }

/*Remove Ads*/
.ego_column { display:none ; }
}
