css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



/*FIXME: new from tumblelog, queue, tag editor avatars*/





body {



background-color: transparent!important;



background-image: url('http://i270.photobucket.com/albums/jj94/0h_its_B3kka/9014nc6.gif');



background-position: 50% -310px;



background-repeat: repeat;



background-attachment: fixed;



}





div.l-header {



border-radius: 35px!important;



background-color: rgb(0,0,0)!important;



opacity: .7!important;



}





.return_to_top_icon {



background: -600px -190px url(http://img546.imageshack.us/img546/7392/dashboardmastersprite.png)!important;



}





#right_column .radar .content {



background: url(http://img833.imageshack.us/img833/9994/radarspriteorange.png) repeat-y;



border-radius: 10px!important;



}





#right_column .controls_section.promo .title {



color: #FF6633!important;



}





#right_column .controls_section.promo .description_and_title {



background-color: rgb(253,117,75)!important;



}





#right_column .controls_section.promo .description {



color: rgb(255,219,176)!important;



}





#right_column .radar .content .mask {



background: url(http://img833.imageshack.us/img833/9994/radarspriteorange.png) no-repeat;



border-radius: 10px!important;



}





#upload_image_form div {



background-color: #FFFFFF!important;



}





.camera {



background-color: #FFFFFF!important;



}





.no_posts_found, h1.dashboard_header {



color: #000000!important;



}





#right_column .controls_section li {



background: rgb(255,140,0)!important;



}





#right_column .radar .attribution .username {



color: rgb(0,0,0)!important;



}





#right_column .radar .attribution .label_and_follow a {



color: rgb(0,0,0)!important;



}





#right_column .radar .attribution .label_and_follow {



color: rgb(0,0,0)!important;



}





#right_column .controls_section li .blog small {



color: rgb(255,231,204)!important;



}





#right_column .controls_section .with_subtitle small {



color: rgb(255,219,176)!important;



}





#search_query {



background-color: rgb(255,140,0)!important;



}





#dashboard_controls a, #dashboard_controls .control {



color: #FF8C00!important;



text-shadow: none!important;



font-weight: bold!important;



}





.dashboard_controls_arrow_first_page {



background: url('http://img193.imageshack.us/img193/4001/dashboardcontrolsarrowf.png')!important;



}





.dashboard_controls_arrow_left {



background: url('http://img810.imageshack.us/img810/1028/dashboardcontrolsarrowl.png')!important;



}





.dashboard_controls_arrow_right {



background: url('http://img405.imageshack.us/img405/180/dashboardcontrolsarrowr.png')!important;



}





#auto_pagination_loader {



background: transparent!important;



border: 0px!important;



font-weight: normal;



font-family: Georgia, serif;



font-style: italic;



color: black;



}





#auto_pagination_loader img {



display: none;



}





.post_avatar {



border: 0px!important;



border-radius: 10px!important;



border-top-right-radius: 0px!important;



border-bottom-left-radius: 0px!important;



}





ol#posts li.post span.quote {



font-size: 130%;



font-style: italic;



font-family: georgia, serif;



}





#right_column .controls_section .selected {



background: rgb(255,140,0)!important;



}





#right_column .controls_section .recessed {



background: rgb(205,102,0)!important;



}





#posts .notification {



background-color: rgb(205,102,0)!important;



border: solid 1px rgb(205,102,0)!important;



}





#posts .notification .notification_type_icon {



background: 0 0 url('http://img707.imageshack.us/img707/8443/dashboardmasterspriteor.png') no-repeat transparent;



}





#posts .notification.single_notification {



border: solid 1px rgb(205,102,0)!important;



}





#posts .notification a:not(.avatar_frame) {



border-bottom: 1px solid rgb(165,80,0)!important;



}





#posts .notification blockquote {



border-left: solid 3px rgb(165,80,0)!important;



}





#posts .notification .nipple {



border-right: 6px solid rgb(205,102,0)!important;



}



div.l-header .selection_nipple {



border-bottom: 12px solid rgb(255,140,0)!important;



}





#posts .avatar_frame {



background-color: transparent!important;



border: 0px!important;



}





#posts .post .permalink {



background: none!important;



}





#footer div {



color: rgb(0,0,0)!important;



}





#footer div a {



color: rgb(0,0,0)!important;



}



}







@-moz-document url-prefix('http://www.tumblr.com/dashboard'), url-prefix('https://www.tumblr.com/dashboard')   {



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: transparent!important;



}



}





