css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {
    body, td, th, input, a, div {font-family:'Consolas',sans-serif!important;}
    .s_nav, #frs_good_nav, div.l_banner, .p_ding_down, .p_mtail, #frs_banner_ad {display:none;}
    a {text-decoration:none!important;}
    .thread_title a {font-weight:bold;color:#49c!important;}
    .thread_alt {background-color: #ffffee!important;}
    .thread_alt td {border-top:1px solid #f6f6aa;border-bottom:1px solid #f6f6aa;}
    #thread_list_table tbody tr:hover {background-color: #ffffcc!important;}
    #thread_list_table td {color:#aaa;}
    #thread_list_table td:nth-of-type(5n+4) a,
        #thread_list_table td:nth-of-type(5n) a {color:#aaa!important}
    div.d_sign_split, div.d_sign_split+*, .fools_btn {display:none;}
    td.d_content {padding:0 0 0 15px!important;}
    p.d_floor {float:right;}
    .d_post_content img {margin:0!important;position:relative;top:8px;}
    blockquote.d_quote {margin-top:10px!important;}
    fieldset {border-radius:4px;margin-bottom:5px!important;}
    legend {font-weight:bold;}
    .d_ding_btn_default, .d_ding_btn_highlight{margin-left:0!important;position:relative;left:-100px;}
    td.d_tail {position:absolute;right:10px;}
    ul.p_tail {position:relative;top:-25px;left:0;}
    a.p_author_face img {max-width:60px;max-height:60px;}
    div.icon_relative.d_avatar_frame {width:auto!important;height:auto!important;}
    td.d_author,td.d_author_anonym {width:80px!important;}
    div.d_post_split {padding-bottom:5px!important;}
    div.d_split_line {margin:0!important;border-bottom:1px dotted #ccc!important;}
    div.p_thread, div.p_postlist, div.p_ding_last, div.l_thread, div.p_ding_down {
        width:auto!important;margin-right:240px!important;
    }
    div.p_searchbox {display:none;}
    div.l_post:hover {background:#f0ffee;}
    div.l_info {padding-left:15px!important;}
    div.d_avatar_frame {width:auto!important;height:auto!important;background:none!important;}
}
