css:
@-moz-document domain("www.myspace.com")
{

#header,
#splash_mainLeft,
#ad300x100,
#splash_coolNewPeople,
#ctl00_Main_SplashDisplay_CMS_SplashHome_Gads_AC,
#ctl00_Main_SplashDisplay_featuredVideos_CMS_videos,
#ctl00_Main_SplashDisplay_CMS_SplashHome_Gads_DE,
#splash_mainBottom, #home_gafc.googleafc, #internationalFooter
{display: none !important; height: 0 !important; width: 0 !important; visibility: collapse !important; font-size: 11px !important;}

}

@-moz-document domain("home.myspace.com")
{

/* my mail */

#home_messages table tr td {padding:2px !important;}

/* friend space */

#home_friends {margin-top:-13px !important; padding:0 !important;}

/* smaller friends */
.w100 {width:90px !important; padding:5px !important; height: 60px !important; overflow: hidden !important;}
.w100 a {font-size:11px !important; visibility: visible !important; display:inline !important; margin:-100px !important; text-align:left !important;} 
.w100 a img {clip:rect(0 90px 60px 0) !important; display:inline !important; margin:0px !important;}

/* bulletins */

#home_bulletins th {font-size:11px !important; width:auto !important;}
#home_bulletins tr td {font-size:11px !important; width:auto !important; padding:2px !important;}
#home_bulletins tr td a {font-size:11px !important;}


/* Google image, ads, etc */
img[alt="Powered by Google"],
#home_greybox,
#row0,
#advert,
#advert object,
#advert embed,
#advert iframe,
#squareAd,
#squareAd iframe,
#ad-wrap,
.googleafc,
/* unnecessary MySpace modules */
#home_featured_comedy,
#home_coolNewVideos,
#home_featured_music,
#home_featured_books,
#home_featured_filmmaker,
#ctl00_Main_ctl00_cvFeaturedFilm,
#splash_coolNewPeople,
#splash_profile,
img.ImgOnlineNow,
#home_searchAddressBook,
#home_setHomePage,
#StatusBox,
#home_infoBar,
#rightlinks, #home_gafc.googleafc, #tkn_leaderboardDiv
{display: none !important; height: 0 !important; width: 0 !important; visibility: collapse !important;}

}

@-moz-document domain("schools.myspace.com")
{
#tkn_leaderboardDiv
{display: none !important; height: 0 !important; width: 0 !important; visibility: collapse !important;}
}



