css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







*{text-shadow:none !important; color:#565656 !important}







#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies, #dashboard_tumblelog_settings, #dashboard_inbox{



background-image:url('http://static.tumblr.com/eczypht/S7Um04ett/bg_12.png') !important;



background-color:#fff !important;



background-repeat:repeat !important;



background-position: left top !important;



background-attachment:fixed !important;



font-family:verdana, Serif !important;



}







#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: rgba(0,0,0,0) !important;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background-color:transparent !important



}







#left_column #posts .post .permalink {



background-color:#efefef !important;



}







#left_column #posts .post .post_avatar,



#left_column #posts .post .sub_avatar .sub_avatar_inner {



background-color: #fbfbfb !important;



border-bottom: 0px !important;



}







#left_column #posts .post{



border-bottom:4px #444 dotted !important;



}







#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#fbfbfb !important;



background-image:none !important;



border:0px !important



}







#nav .nav_item .nav_menu {



border-bottom:0px !important



}







#nav .nav_item .nav_menu a {



color:#888 !important;



border-top-color:#eee !important;



}







#nav .nav_item .nav_menu a:hover {



color:#ba2a41 !important;



}







#footer, #footer a {



color:#666 !important;



background-color:#fbfbfb !important;



background-image:none !important;



}







.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {



color:#fff !important



}











#right_column li{



background: transparent !important;



border:0px !important;



background:rgba(256,256,256,0.3) !important;



}







#right_column .dashboard_nav_item {



border-bottom:0px solid #eee !important;



background-image:none  !important



}







#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fff !important;







border:0px !important;



}



#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {



background:rgba(256,256,256,0.7) !important;







}











#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#fff !important ;



                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }







#show_radar a, #hide_radar a,



                        #hide_recommended_blogs a, #show_recommended_blogs a {



                            color:#fff !important;



                        }











#left_column #posts .notification {



                        background-color:#fbfbfb !important;



                        color:#666 !important;



                        border-bottom-color: #eee !important



                    }







#left_column #posts .notification .block {



                            color:#333 !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #f7f7f7 !important;



                    }



#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#ececec !important;



                    }



#left_column #posts .notification a {



                        color:#565656 !important;



                        border-bottom:0px !important;



                    }



#left_column #posts .notification blockquote {



                        border-left-color:#eee !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#fbfbfb !important;



opacity:0.5 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }







.dashboard_switch_blog_menu_header{



background-color:#fbfbfb !important;



color:#666 !important;



}



.dashboard_switch_blog_menu_item{



background-color:#fbfbfb !important;



color:#666 !important;



border:0px !important;



}



.dashboard_switch_blog_menu_footer{



background-color:#fbfbfb !important;



color:#fff !important;



}



.dashboard_switch_blog_menu_wrapper{



background-color:#fbfbfb !important;



}







#new_post_notice_container{



color:#fff !important



}







#new_post_notice_container a{



color:#fff !important



}







#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{



background-image:none !important



}



.notification_type_icon{



display:none !important



}











h1,h2,h3,h4,h5,h6{color:#333 !important}















.new_post_label{



font-size:13px !important;



padding-top:8px !important;



text-transform:lowercase !important;



letter-spacing:2px !important;



font-style:italic !important;



text-shadow: none !important;



font-weight:normal !important;



margin:0px !important;



font-family:verdana !important;



color:#cfccab !important



}



.new_post_label:after{



content:"" !important



}



#new_post_label_text{margin-left:0px !important}



#new_post_label_photo{margin-left:-2px !important}



#new_post_label_quote{margin-left:3px !important}



#new_post_label_audio{margin-left:4px !important}



#new_post_label_link{margin-left:0px !important}



#new_post_label_video{margin-left:0px !important}



#new_post_label_chat{margin-left:1px !important}











.new_post_label:hover{



color:#c66d6d !important



}



.arrow{display:none !important}



.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}



#new_post{



background: none center top no-repeat;



background-color:#fff !important;



}



img[src*=""]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



}







.tab a{



color:#fff !important;



}







.items li a {color:#fff !important}



.last_item a{color:#fff !important}







.selected a{text-shadow:none !important; color:#fff !important}



.recessed a{text-shadow-color:none !important; color:#fefefe !important}







.right_column_section li{background:rgba(0,0,0,0.1) !important; border:0px !important; color:#fff !important}



.right_column_section li a{color:#fff !important}



.right_column_section li a:hover{background:rgba(0,0,0,0.5) !important}



.notification, .avatar_frame, .nipple{border:0px !important}



.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:4px !important; margin-right:5px !important;-webkit-border-radius:5px 5px 5px 5px !important;-moz-border-radius:5px 5px 5px 5px  !important}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0,0,0,0) !important}



.dashboard_options_form, .dashboard_options_form_disclaimer{ border:0px !important; color:#fff !important; font-weight:bold !important; text-shadow:1px 1px 1px #444 !important}











.selection_nipple{



display:none !important;



}











.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:4px !important; margin-right:5px !important;-webkit-border-radius:0px 5px 0px 0px !important;-moz-border-radius:0px 7px 7px 0px  !important}







#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}







#left_column #posts .post .sub_avatar_inner {







-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;







}







#right_column li:hover {



   opacity:1; 



   background: rgba(256, 256, 256, 0.6) !important;



 }















#logo {







height: 0 !important;







	width: 0 !important;







	/* these numbers match the new image's dimensions */







	padding-left: 196px !important;







	padding-top: 44px !important;







	background: url(http://static.tumblr.com/eczypht/Fuvly7myl/1234_logo.gif) no-repeat !important;















}











::selection {



        background: #F84E82; /* Safari */



color:#CADE65



        }



::-moz-selection {



        background: #F84E82; /* Firefox */



color:#CADE65}







.tab_bar a{color:#fff !important}







#new_post_notice_container{



color:#fff !important



}







#new_post_notice_container a{



color:#fff !important



}







ul#user_channels{color:#fff !important}



#right_column a{color:#ffffff !important}











}
