css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.aeriagames.com") {
    body {
        background:   -moz-radial-gradient(black 15%, transparent 16%) 0 0, -moz-radial-gradient(black 15%, transparent 16%) 8px 8px, -moz-radial-gradient(rgba(255,255,255,.1) 15%, transparent 20%) 0 1px, -moz-radial-gradient(rgba(255,255,255,.1) 15%, transparent 20%) 8px 9px !important;
        background-color:#282828 !important;
        background-size:16px 16px !important;
    }

    #auxCol iframe {
        height : 800px !important;
        width  : 317px !important;
    }
/* Removing the advertisment */
    #auxCol #auxColRectangleAd {
        display :none !important;
    }

}

@-moz-document domain("shout.aeriagames.com") {
    #shoutboxRender #sbMessages {
        height: 500px !important;
        overflow-x: auto !important;
        overflow-y: auto !important;
    }
}
