css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies, #dashboard_tumblelog_settings, #dashboard_inbox{



background:#1c171f url('http://i43.tinypic.com/10dv7f9.jpg') no-repeat fixed top right !important;



}



.dude{display:none !important}



#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, #main_tag #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background-color:rgba(0,0,0,0.0) !important;



}



*{border:0px !important}







#dashboard_edit_post{



background-color: #000000 !important



}



#dashboard_nav_following, #dashboard_nav_likes{ font-size:10px !important}

#right_column {width:200px !important; text-align:left !important; margin-right:30px !important}



#right_column .dashboard_nav_item {



border-bottom:0px solid #eee !important;



background-image:none  !important



}







#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fff !important;



}







#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#fff !important;



text-shadow: #000 0px 0px 2px !important;



font-family: Georgia !important;



font-style: italic !important;



}



#right_column .dashboard_nav_item .dashboard_nav_title{



font-family: arial !important;



font-style: normal !important;



font-weight:bold !important;



color: #fff !important



}



#right_column .dashboard_nav_item ul.dashboard_subpages li{margin-bottom:10px !important;}



#right_column .dashboard_nav_item ul.dashboard_subpages li a {



color:#fff !important;



text-shadow: 1px 1px 2px #000 !important;

-moz-border-radius: 10px !important;

background-color:rgba(0,0,0,0.3) !important;

margin:0px !important;



padding:3px  !important



}



#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover{



background-color:rgba(256,256,256,0.5) !important;



color:#333 !important;



text-shadow: none !important;



-moz-border-radius: 10px !important;



padding:3px  !important



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#888 !important ;



                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }















#left_column #posts .notification .avatar {



                        border:transparent !important;



                        background-color:#333 !important;



opacity:0.5 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }



.post{opacity:1 !important; border-bottom:0px !important; color:#333}



.post:hover{opacity:1 !important}



.permalink{background-color: #1c171f !important;}



.active{background-color:transparent !important}



.nav_item_nipple{display:none !important}



.nav_menu{background-color:#222 !important; border-bottom:0px !important}







#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#222 !important;



background-image:none !important;



border:0px !important



}







#nav .nav_item .nav_menu {



border-bottom:0px !important



}







#nav .nav_item .nav_menu a {



color:#666 !important;



border-top-color:#151515 !important;



}







#nav .nav_item .nav_menu a:hover {



color:#888 !important;



}







.dashboard_switch_blog_menu_header{



background-color:#222 !important;



color:#666 !important;



background-image:none !important



}



.dashboard_switch_blog_menu_item{



background-color:#222 !important;



color:#666 !important;



border:0px !important



}



.dashboard_switch_blog_menu_footer{



background-color:#222 !important;



color:#666 !important;



background-image:none !important



}



.dashboard_switch_blog_menu_wrapper{



background-color:#222 !important;



}







#new_post_notice_container{



color:#999 !important



}







#new_post_notice_container a{



color:#111 !important



}







#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{



background-image:none !important



}







#footer{



background: rgba(10,10,10,0.6) !important;



background-image:none !important



}















ol#posts li.post.new_post {



background: #000 url('http://i44.tinypic.com/33y6rtx.jpg') center center no-repeat;



}







img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



}



.new_post_label{



text-shadow: 1px 1px 2px #000 !important;



padding-left:1px !important;







text-transform: lowercase !important;







letter-spacing:0px !important;











font-family:Georgia !important;







font-size: 20px !important;







font-style:italic !important;







margin:0px !important;







background-color:rgba(0,0,0,0.4) !important;



}











.arrow{display:none !important}











#left_column #posts .notification {



                        background-color:#111 !important;



                        color:#333 !important;



                        border-bottom-color: #222 !important



                    }







#left_column #posts .notification .block {



                            color:#454545 !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #000 !important;



border-bottom-color: #222 !important



                    }



#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#111 !important;



                    }



#left_column #posts .notification a {



                        color:#666 !important;



                        border-bottom:0px !important;



                    }



#left_column #posts .notification blockquote {



                        border-left-color:#444 !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#222 !important;



opacity:0.5 !important;



                    }







#left_column #posts .notification .avatar:hover{



opacity:1 !important;



                    }



.notification_type_icon{opacity:0.1 !important}



.like_icon{display:none !important}







h1,h2,h3,h4,h5,h6{color:#555555 !important}



a{color:#555555 !important}



a:hover{color:#888888 !important}



#new_post a{color:#fff !important}



#new_post a:hover{color:#61b09a !important}

.selection_nipple{

display:none !important;

}





.tab a{

color:#fff !important;

}



.items li a {color:#444 !important}

.last_item a{color:#444 !important}



.selected a{text-shadow:none !important; color:#fff !important}

.recessed a{text-shadow-color:none !important; color:#fefefe !important}



.right_column_section li{background:rgba(0,0,0,0.4) !important; border:0px !important}

.right_column_section li a{color:#555 !important}

.right_column_section li a:hover{background:rgba(0,0,0,0.6) !important}

.notification, .avatar_frame, .nipple{border:0px !important}

.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:4px !important; margin-right:5px !important;-webkit-border-radius:5px 5px 5px 5px !important;-moz-border-radius:5px 5px 5px 5px  !important}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0,0,0,0) !important}

.dashboard_options_form, .dashboard_options_form_disclaimer{ border:0px !important; color:#fff !important; font-weight:bold !important; text-shadow:1px 1px 1px #444 !important}

}
