css:
.t_hdbgimg
{
background-image: -webkit-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: -moz-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: -o-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: -ms-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: linear-gradient(top,whiteSmoke,#F1F1F1);
}
.t_hdt, h1, .t_hdt a, .t_hdt a:visited, .t_hdt a:hover, .t_hdt a:link {
color: #444;
}
#InboxToolbar{width:900px !important;}
#folderList {font-size: 11px;text-transform: uppercase;}
.Unmanaged .WithSkyscraper #MainContent {
margin-right: 0px;
}
li[aid=saveDraft], #rteMode, #CleanUp, #MarkAs, #MoveTo, #Categorize, li[aid=scriptCancelMessage], li[aid=emptyFolder], li[aid=markAsNotJunk]
{
margin: 3px 0 0 0;
}
#NewMessage, #DeleteMessages, #SendMessage, li[aid=saveDraft], #rteMode, #CleanUp, #MarkAs, #MoveTo, #Categorize, li[aid=scriptCancelMessage] , li[aid=emptyFolder] , li[aid=markAsNotJunk]
{
line-height: 25px;
border: 1px solid 
transparent;
color: white;
text-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
text-transform: uppercase;
-webkit-border-radius: 2px;
cursor: pointer;
font-size: 10px;
font-weight: bold;
text-align: center;
margin-right: 3px;
min-width: 54px;
outline: 0;
padding: 0 3px;
}
#NewMessage, #SendMessage
{
background-image: -webkit-linear-gradient(top,#DD4B39,#D14836);
background-image: -moz-linear-gradient(top,#DD4B39,#D14836);
background-image: -o-linear-gradient(top,#DD4B39,#D14836);
background-image: -ms-linear-gradient(top,#DD4B39,#D14836);
}
#DeleteMessages, li[aid=saveDraft]  , #rteMode, #CleanUp, #MarkAs , #MoveTo, #Categorize, li[aid=scriptCancelMessage], li[aid=emptyFolder], li[aid=markAsNotJunk]
{
background-image: -webkit-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: -moz-linear-gradient(top,whiteSmoke,#F1F1F1);
background-image: -o-linear-gradient(top,whiteSmoke,#F1F1F1);
color: #444;
border: 1px solid gainsboro;
border: 1px solid rgba(0, 0, 0, 0.1);
}
li[aid=scriptCancelMessage]
{
position: absolute;
top:0;
left:460px;
}
#NewMessage:hover, #DeleteMessages:hover
{
-webkit-box-shadow: 0 1px 3px rgba(0, 0, 0, .3);
}
#PrintMessages,#Refresh, #MarkAsJunk, .ToolbarHelpLink, #AfterMoveToPipe {display: none;}
 #toolbarContainer {padding: 0 0 10px 0}
 table.InboxTable tr{height: 40px !important;font-size: 14px !important;
font-family: Segoe UI !important;}
.staticItem , #mlPinBoard{height: 0px !important;}
#titleSearchContainer
{
border-radius: 5px 5px 0 0;
}
