css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {


#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta div.l-content, #dashboard_inbox div.l-content,#dashboard_tumblelog_settings div.l-content,#dashboard_followers, dashboard_tagged #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:transparent !important
}


#dashboard_index,#dashboard_tagged,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_members,#dashboard_inbox,#dashboard_tumblelog_settings,#dashboard_directory {
background:#bfbfbd url(http://www.pictureshack.us/images/791bgc.jpg) no-repeat right bottom fixed !important
}

body{

    background: url('http://www.pictureshack.us/images/7051bgcf.png')no-repeat right bottom fixed !important;

}

ol#posts li blockquote,

body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;

padding-left: 10px !important;

border-width: 4px !important;
border-color: #BEC4C7 !important;
}

ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #B9C9D1 !important;
}
ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #cacbcb !important;

}
ol#posts li blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #e1e7ea !important;


}
ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #e7ecef !important;
}
ol#posts li.notification.single_notification {
border-bottom: 1px solid #000000;
}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: transparent !important;

  }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background: transparent !important;
    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;
    border-radius: 20px;
    margin: auto;
    padding: 40px auto;
  }
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
width:0;
height:70px;
padding-right:0px;
opacity: 0;
}

.new_post_label, .new_post_label_icon {
background: none !important;
}

li#new_post.post.is_mine.with_avatar.new_post {
background-image: url('http://assets.tumblr.com/images/new_post_icons_no_labels.png');
background-position: 46% 70%;
background-repeat: no-repeat;
}

a#new_post_label_text.new_post_label,
a#new_post_label_photo.new_post_label,
a#new_post_label_quote.new_post_label,
a#new_post_label_link.new_post_label,
a#new_post_label_chat.new_post_label,
a#new_post_label_audio.new_post_label,
a#new_post_label_video.new_post_label {
padding-top: 68px !important;
padding-bottom: 11px !important;
}

h1 {
color: #333333;
text-shadow: 0 0 3px #ffffff;
}
.no_posts_found, h1.dashboard_header {
color: #666666;
}
img#content_top, img#content_bottom {
display: none;
}
#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
    background: none;
  }
#right_column .dashboard_nav_item {
    background: none;

    background-attachment: initial;

    background-position-x: 50%;

    background-position-y: 100%;

    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}

#right_column .dashboard_nav_item #hide_radar {

background-image: none;
background-repeat-x: repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: transparent;
}
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: transparent ;
}

#nav .nav_item.active {
    background: rgba(0,0,0,0.2) !important;

  }
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

    background: rgba(0,0,0,0.2) !important;
  }
ol#posts li.notification.alt {
    background-color: rgba(0,0,0,0.5);
}
ol#posts li.notification.first_notification {
    background: #9ea0a4;
}

ol#posts li.notification {
    background: #9ea0a4;
    border-bottom: 1px solid #000000;
}
ol#posts li.notification.last_notification {
    border-bottom: 1px solid #000000;
}
ol#posts li.post .permalink {
    background: rgba(0,0,0,0.2);

}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {

background: #9ea0a4;
border: solid #000000;
}
ol#posts li.post .post_avatar{

border-bottom: 0px solid #818485;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #000000;
border-bottom: 1px solid #000000;
}
#right_column a#dashboard_switch_blog_menu_current:hover {
background: url(http://i55.tinypic.com/34nlldd.png);
}
#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
background: url(http://i55.tinypic.com/34nlldd.png);
}
form.dashboard_options_form {
background: #9ea0a4;
border-bottom: 1px solid #000000;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}
form.dashboard_options_form .option_container {
border-top: 1px solid #000000;
}
.no_posts_found, h1.dashboard_header {
color: #333333;
}
.answer_container {
background-color: #000000;
border-bottom: 0px solid #d6dade;
}
#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 2px solid #333333;
}
#nav .nav_item .nav_menu a {
border-top: 1px solid #333333;
}
#auto_pagination_loader {
background: none;
border-top: 0px solid #424850;
}
}
@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    padding: 40px 0;
  }
}
