css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 

 {

body {background: url('http://static.tumblr.com/rodxhpu/7Hxm0a5rd/adelediva-.png') top left #8a9286 no-repeat fixed !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#05051e !important; background: rgba(191,170,141,.6) !important;}

*{text-shadow:none !important; color:#393322!important;}

ol#posts li.notification{background-color: #425e52 !important; border-bottom: 2px solid #6D523A !important; color: #6D523A !important;opacity: .80;}

#right_column a#dashboard_switch_blog_menu_current:hover {background:#96afa7 ;opacity: .80;}

#nav .nav_item.active{background-color:#adedd2}

#left_column #posts .post .permalink{background-color:#9bbbb2;opacity: .80;}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{background-image:none !important}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 

	background: none !important;

	width: 0px !important;

	height: 0px !important;

	margin-top: 0px !important;

	border-bottom: solid 11px #c8ded8!important;

	border-right: 11px solid transparent !important;

	border-left: 11px solid transparent !important; }

.selection_nipple:not([class="selection_nipple white"]) {

	border-bottom: solid 11px #3d3c50!important; opacity: .8;}

.notification_type_icon{display:none !important}

.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue, 

.selected .likes {

	color: #c3b098!important; 

	background: #97aea7!important;}

a.explore_more_tags,

a.add_and_remove,

a.customize,

a.mass_editor

	{background-color:#acc6be!important;  

	background-image:none !important;

	border:0px !important}

div.gradient,

div.dashboard_nav_item, 

a.permalink

	{background-color:#8bc1aa!important;

	opacity:.9; 

	background-image:none !important;

	border:0px !important}

.content.is_media, 

.mask.top, 

.mask.bottom {

	background: none !important; }

.post.photo_post .photo, 

.post.photo_post .video_thumbnail {

	border-radius: 16px !important;}

.post.photo_post .photo, 

.post.photo_post .video_thumbnail 

	{-moz-box-shadow: 1px 1px 3px #000000 !important;}

#auto_pagination_loader

	{background: #85c4aa !important;}

div.return_to_top_icon{	background: none !important;

	width: 0px !important;

	height: 0px !important;

	margin-top: 0px !important;

	border-bottom: solid 30px white !important;

	border-right: 15px solid transparent !important;

	border-left: 15px solid transparent !important;}

#left_column #posts .notification {

background-color:#88a594!important;

color:#666 !important;

border-bottom-color: #eee !important}

#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}



.new_post_label_icon {

    background-image: url('http://static.tumblr.com/rodxhpu/cfzm0a5ff/icons-1.png') !important;

    top: 9px !important;

    left: -1px !important;

}



.new_post_label {

    padding-top: 67px !important;

}



#new_post_label_text:active .new_post_label_icon {

    background-position: 2px -5px !important;

}

#new_post_label_photo:active .new_post_label_icon {

    background-position: -73px -5px !important;

}

#new_post_label_quote:active .new_post_label_icon {

    background-position: -148px -5px !important;

}

#new_post_label_link:active .new_post_label_icon {

    background-position: -223px -5px !important;

}

#new_post_label_chat:active .new_post_label_icon {

    background-position: -298px -5px !important;

}

#new_post_label_audio:active .new_post_label_icon {

    background-position: -373px -5px !important;

}

#new_post_label_video:active .new_post_label_icon {

    background-position: -448px -5px !important;






}




#logo {height: 0 !important;

width: 0 !important;

padding-left: 209px !important;

padding-top: 42px !important;

background: none no-repeat !important;}

.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}}
