css:
#sidebar {
	display: none;
}
#pagebody #mainbody {
	width: 980px;
	float: right;
	background-color: #FFF;
	font-family: "Myriad Web Pro", "微软雅黑";
}
.content_navi {
	height: 28px;
	color: #333;
	font-size: 18px;
	font-weight: normal;
	padding: 10px;
}
.list_content_body {
	width: 980px;
	overflow: auto;
	zoom: 1;
	padding-top: 2px;
	padding-right: 0;
	padding-bottom: 5px;
	padding-left: 0;
}
.list_content_body tr {
	line-height: 38px;
	height: 38px;
	border-bottom-width: 1px;
	border-bottom-style: dotted;
	border-bottom-color: #E3E3E3;
}
/*---------内页-----*/
#content_blue_body {
	width: 980px;
	zoom: 1;
}
.content_blue_topbar_l,.content_bluebar_c,.content_blue_topbar_r,.content_blue_bottombar_l,.content_bluebar_c,.content_blue_bottombar_r,.content_brown_topbar_l,.content_brownbar_c,.content_brown_topbar_r,.content_brown_bottombar_l,.content_brownbar_c,.content_brown_bottombar_r{
	display: none;
}
.content_blue_middlebar {
	overflow: hidden;
	zoom: 1;
	background-color: #FFF;
}
.content_blue_body{
	padding: 8px;
	overflow: auto;
	zoom: 1;
	background-color: #b5e3ee;
	-moz-border-radius: 6px;
	-webkit-border-radius: 6px;
	border-radius: 6px;
	border: 1px solid #a2d4e0;
}
.user_info {
width: 590px;
}
.content_info,.content_brown_info{
	padding: 15px;
	zoom: 1;
	line-height: 22px;
	word-break: break-all;
	font-size: 14px;
	background-color: #e8f7fc;
	-moz-box-shadow:inset 0px 0px 5px #d7d7d7;
	-webkit-box-shadow:inset 0px 0px 5px #d7d7d7;
	box-shadow:inset 0px 0px 5px #d7d7d7;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
}
#content_brown_body{
	width: 980px;
}
.content_brown_middlebar {
	padding: 0px;
}
