css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dropbox.com") {
html, body, #browse-global-actions-bar, #gs {
background: #1b1b1b !important;
color: #eee !important;
}
a {
color:#61BEFF !important;
}
a:hover {
color:#FF5950 !important;
}
.browse-file.file-select {
background: #1b1b1b !important;
color: #eee !important;
border-color: #eee !important;
}
.browse-file:hover{
background-color: #222 !important;
}
#browse-root-actions {
background: #1b1b1b !important;
color: #eee !important;
box-shadow: none !important;
border: none !important;
}
#browse-sort {
background: #1b1b1b !important;
color: #eee !important;
box-shadow: none !important;
border: none !important;
}
#page-header {
background: #1b1b1b !important;
color: #eee !important;
}
#browse-location {
background: #1b1b1b !important;
color: #eee !important;
}
.sprite.s_gift.link-img, .sprite.s_door_in.link-img {
display: none !important;
}
.sprite.s_login_arrow.link-img {
display: none !important;
}
.chat-bubble {
background: #333 !important;
color: #eee !important;
border: 1px solid #eee !important;
}
.hotbox.clearfix, .clearfix.candybox, .email, #browse-search-input, label, #more_global_actions_button, #revisions_button, #copy_button, #move_button, #rename_button, #delete_button, #download_button, #copy_url_button, #upload_button, tr, li.selected, .browse-file, .subtab:hover, .explanation, #new_folder_button, #global_share_button, #global_photos_button, #show_del_button, #learn-more {
background: #333 !important;
color: #eee !important;
}
.clearfix.candybox > img {
display: none !important;
}
.hotbox.clearfix, .clearfix.candybox {
box-shadow: none !important;
border:1px solid #eee !important;
}
#browse-search-input {
box-shadow: none !important;
}
#left {
color: #eee !important;
}
#events-wrapper {
background: #1b1b1b !important;
color: #eee !important;
}
tr, li.selected, .browse-file, .subtab:hover {
background: #333 !important;
color: #eee !important;
}
h2, h4, h1, strong {
background: inherit !important;
color: #eee !important;
}
.gs-row {
border-top:1px solid #eee !important;
border-bottom:1px solid #eee !important;
}
#subtabs-container {
background: #1b1b1b !important;
color: #eee !important;
border-bottom:1px solid #eee !important;
}
#subtabs {
background: none !important;
color: #eee !important;
}
.subtab {
background: #444 !important;
color: #eee !important;
}
.subtab.selected:hover {
background: #333 !important;
color: #eee !important;
border-bottom: none !important;
}
.subtab.selected {
background: #111 !important;
color: #eee !important;
border-bottom: none !important;
}
#db-logo {
display: none !important;
}
.sprite.s_star.link-img {
display: none !important;
}
#login_email_elm, #login_password_elm, .create-account {
background: #333 !important;
color: #eee !important;
box-shadow: none !important;
}
#login-hover-link {
background: #333 !important;
border:1px solid #eee !important;
}
#revisions_button:hover, #copy_button:hover, #move_button:hover, #rename_button:hover, #delete_button:hover, #download_button:hover, #copy_url_button:hover, #upload_button:hover, #new_folder_button:hover, #global_share_button:hover, #global_photos_button:hover, #show_del_button:hover {
background: #222 !important;
color: #eee !important;
}
}
