// CUSTOM DASHBOARD THEME - THE HUNGER GAMES //

Check http://omgwtfhedwig.tumblr.com/ for more themes.

// INSTRUCTIONS //

1. Install Stylish
2. Click the 'Install with Stylish' button below
3. Check out your new Tumblr dashboard!

css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



/* -- Coded by Omgwtfhedwig.tumblr.com -- */

/* -- Enjoy but don't copy or redistribute -- */

/* -- Last updated: 06/13 -- */







/* -- Left and Right column container -- */

#dashboard_index #container, #dashboard_edit_post #container, #dashboard_submissions #container, #dashboard_drafts #container, #dashboard_post_queue #container, #dashboard_followers #container, #dashboard_members #container, #dashboard_likes #container, #dashboard_following #container, #dashboard_tumblelog_settings #container, #dashboard_inbox #container, #main_tag #container, #main_tag_beta #container, #tagged_display #container, #dashboard_useraction_followers #container, #dashboard_posts_likes #container, #dashboard_messages_inbox #container, #dashboard_messages_submissions #container {

margin-top: auto !important;

margin-right: 5px !important;

margin-bottom: auto !important;

padding-top: 0pt;

padding-bottom: 0pt;

}





#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, #dashboard_likes, #dashboard_inbox, #dashboard_tumblelog_settings, #dashboard_settings, #dashboard_following, #dashboard_messages_inbox, #main_tag, #main_tag_beta, #tagged_display, #dashboard_posts_likes, #dashboard_useraction_followers, #dashboard_messages_submissions {

background-image:url('http://i39.tinypic.com/2r3wmk9.jpg') !important;

background-color: #000000 !important;

background-repeat: no-repeat !important;

background-attachment: fixed !important;

background-position: bottom left !important;

}





/* -- BG Opacity -- */

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content,#dashboard_followers div.l-content, #dashboard_tumblelog_settings div.l-content, #dashboard_members div.l-content, #dashboard_likes div.l-content, #dashboard_inbox div.l-content, #main_tag div.l-content, #main_tag_beta div.l-content, #dashboard_messages_inbox div.l-content, #tagged_display div.l-content, #dashboard_posts_likes #container, #dashboard_useraction_followers #container, #dashboard_messages_submissions #container {

background: rgba(100,100,100,0.0) !important;

}





/* -- VECTOR PICS -- */

.new_post_label {

font-size: 10px !important;

margin-right: 0px !important;

font-family: Georgia, sans-serif !important;

text-transform: uppercase !important;

color: #000000 !important;

letter-spacing: 0px !important;

}



.new_post_label_icon {

background-image: none !important;

}



#new_post {

background: #ffffff url('http://i41.tinypic.com/fvjz8o.jpg') top center no-repeat !important;

}









div.l-content.clearfix {

background: transparent !important;

}



#dashboard_useraction_following div.name a, div.description span, div.crushes_subhead, div.grey_note a {

color: #ffffff !important;

}



#dashboard_useraction_following  #content_bottom {

display: none !important;

}



.tag_page_header {

width: 900px !important;

height: 55px;

}





#content_top, #content_bottom  {

width: 900px !important;

height: 20px !important;

background: transparent !important;

display: none;

}









div.selection_nipple {

display: none !important;

}



/* -- Entire sidebar -- */

#right_column {

background-color: transparent !important;

float: right !important;

width: 215px !important;

color: #ffffff !important;

}



/* -- BAR -- */

div#right_column,div#right_column:after {

background: none !important;

}



#right_column .dashboard_nav_item {

border-bottom: 0px solid #eee !important;

background-image: none  !important;

}









/* -- Sidebar text colour -- */

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a {

color: #ffffff !important;

}














/* -- Tracked tag X -- */

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

color: #033097 !important;

}





/* -- Radar Pic box -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media {

overflow-x: hidden;

overflow-y: hidden;

width: 180px !important;

}



/* -- Radar Pic box inner -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background-color:  #033097 !important;

background-image: none;

background-repeat: repeat;

background-attachment: scroll;

background-position: 0% 0%;

padding-top: 5px;

margin-right: 10px !important;

padding-bottom: 5px;

padding-left: 5px;

}



/* -- Radar Pic -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media .post img {

max-width: 180px !important;

}







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold, div.radar div.content.is_media div.mask.top, div.radar div.content.is_media div.mask.bottom, div.radar div.content.is_media {

background: none !important;

}





#left_column {

float: left;



}







#left_column #posts .notification .avatar {

border-color: transparent !important;

background-color: #ececec !important;

opacity:0.1 !important;

}







.answer_container {

background-color: #efede5 !important;

margin-left: 20px;

margin-right: 20px;

padding-top: 15px;

padding-right: 15px;

padding-bottom: 15px;

padding-left: 15px;

color: #444444 !important;

font-family: Arial,Helvetica;

font-style: normal;

font-variant: normal;

font-weight: bold;

font-size: 13px;

}











#posts .post .post_question {

background-color: #fdfec9 !important;

color: #455555 !important;

}







#left_column #posts .notification .avatar:hover {

opacity:0.6 !important;

}









/* -- Post text -- */

.post {

opacity:1 !important;

border-bottom:0px !important;

color: #222222 !important

}







.post:hover {

opacity:1 !important

}









.post_permalink, a.permalink {

background: transparent !important;

}





#right_column .dashboard_nav_item a {

color: #ffffff !important;

}





.nav_item_nipple {

display: none !important

}







.nav_menu {

background-color: #b93f27 !important;

border-bottom: 0px !important

}





.dashboard_switch_blog_menu_header {

background-color: #ececec !important;

color: #666666 !important;

}



.dashboard_switch_blog_menu_item {

background-color: #ececec !important;

color: #666666 !important;

border: 0px !important

}





.dashboard_switch_blog_menu_footer {

background-color: #ececec !important;

color: #666666 !important;

}



.dashboard_switch_blog_menu_wrapper {

background-color: #ececec !important;

}





#new_post_notice_container {

color: #ffffff !important

}



#new_post_notice_container a {

color: #ffffff !important

}







#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {

background-image: none !important;

}





/* -- Top links 'Back to first page' 'Page 2' etc -- */

#dashboard_controls {

font-size: 16px !important;

line-height: 1 !important;

height: 35px !important;

color: #ffffff !important;

margin-bottom: 2px !important;

}



#dashboard_controls a, #dashboard_controls .control {

color: #fefefe !important;

text-decoration: none !important;

}



#dashboard_controls a:hover {

color: #cfeeff !important;

}







#footer {

background: rgba(10,10,10,0.0) !important;
margin-left: 80px !important;
color: #a01300 !important;

background-image: none !important;

}



#footer a {

color: #a01300 !important;

}





/* -- 'Previous' 'Next' links -- */

#pagination a {

color: #a01300 !important;

text-decoration:none;

background-color: #ffffff !important;

padding:5px 8px;

margin-left:10px;

white-space:nowrap

}



/* -- 'Previous' 'Next' links -- */

#pagination a:hover {

color: #111111 !important

}





img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {

width:0;

height:70px;

padding-right:250px;

opacity: 0;

}





/* -- 'replied to' 'reblogged' etc BG -- */

#left_column #posts .notification {



background-color: #fdfec9 !important;



color: #111111 !important;



border-bottom-color: #ffffff !important



}







/* -- Alternate 'replied to' 'reblogged' etc BG -- */



#left_column #posts .notification.alt {



background-color: #a01300 !important;



border-bottom-color: #ffffff !important



}







/* -- 'replied to' 'reblogged' etc LINKS -- */



#left_column #posts .notification a {



color: #111111 !important;



border-bottom:0px !important;



}







/* -- 'replied to' 'reblogged' etc Added reply text -- */



#left_column #posts .notification blockquote {



border-left-color: #ffffff !important;



color: #222222 !important;



}







#left_column #posts .notification .block {

color: #ac9c82 !important;

}







#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {

border-bottom-color: #ffffff !important;

}





ol#posts li.notification.first_notification, ol#posts li.notification.last_notification, ol#posts li.notification, ol#posts li.notification.alt, a.avatar_frame {

border: none !important;

}



#left_column #posts .notification .avatar {

border-color:transparent !important;

background-color: #fbfbfb !important;

opacity:1 !important;

}



#left_column #posts .notification .avatar:hover {

opacity:1 !important;

}



#posts .notification .notification_type_icon {

position: absolute;

right: 11px;

top: 10px;



}



#posts .notification .notification_type_icon.answer_icon {

width: 10px;

height: 16px;

}



.notification_type_icon {

}



#posts .notification .notification_type_icon.like_icon {

display: none !important;

}



#posts .notification .report, #posts .notification a.block {



}



#posts .notification:hover a.block {

display: none !important;

}



h1,h2,h3,h4,h5,h6 {

margin-top: 5px !important;

color: #222222 !important

}

/* -- Quote Text -- */
span.quote {
font-size: 16px !important;
font-style: italic !important;
}



}
