css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("last.fm"),
domain("lastfm.es"),
domain("lastfm.de"),
domain("lastfm.fr"),
domain("lastfm.jp"),
domain("lastfm.it"),
domain("lastfm.pl"),
domain("lastfm.com.br"),
domain("lastfm.se"),
domain("lastfm.tr"),
domain("lastfm.ru"),
domain("cn.last.fm") {
 *{font-family: Helvetica, Sans !important;}
 
 div#navigator *{font-size:14px !important;}
 div#navigator .secondary_set *{font-size:13px !important;}
 div#sidebar *{font-size:11px !important;}
 div#home_main *{font-size:11px !important;}
 div#content *{font-size:11px !important;}
 div#home_sidebar *{font-size:11px !important;}
}
