@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photo-forum.net") {
body { background-color: #333333 !important; color: #ccc !important; text-align: center !important;}
  #tableHead, table[bgcolor="#E5E5E5"] {width: 943px !important;}
  table#tableHead td[bgcolor="#F7F7F7"], object, td[background="http://212.36.3.59/site_design/menutail.jpg"],
  a[href="http://photo-forum.net/adrotator/ad_click.php?id=36"] {display: none !important;}
  table {
  margin-right: auto !important;
  margin-left: auto !important;}
  table, tr, td, table.tableThumb {background-color: #000000 !important;}
  a {color:#CC6600 !important;}
  .Link2 {color:#CC6600 !important;}
  .pic  {color:#CC6600 !important;}
  .pic1 {color:#CC6600 !important;}
  .menuAlbum {color:#CC6600 !important;}
  .menuAlbum1 {color:#CC6600 !important;}
  .PhorumNav {
  color: #669999 !important;
  padding-left: 4px !important;}
  font.PhorumNav a font.PhorumNav:hover {
  color:#66CCCC !important;}
  .PhorumListHeader {
  padding-left: 10px !important;
  padding-top: 10px !important;
  padding-bottom: 10px !important;
  font-weight: bold !important;
  color: #777777 !important;
  background-color:#222 !important;}
  a.PhorumListRow, .PhorumSubject, .PhorumTableHeader { color:#999999 !important; }
  a.PhorumListRow:hover, .PhorumSubject:hover{ color:#CCCCCC !important; }
  .PhorumSubject:hover {text-decoration:none !important; }
  td.PhorumListRow {
  color: #999999 !important;
  padding-left: 10px !important;}
  td.PhorumListRow[bgcolor="#ffffff"] {background-color: #222222 !important;}
  img[src="http://212.36.3.59/site_design/forum_fotografia.gif"] {display:none !important;}
  img[src="http://212.36.3.59/site_design/forum_obshtatema.gif"] {display:none !important;}
  img[src="http://212.36.3.59/site_design/forum_tehnika.gif"] {display:none !important;}
  img[src="http://212.36.3.59/site_design/forum_zanovaci.gif"] {display:none !important;}
  img[src="http://212.36.3.59/site_design/forum_konkursi-izlojbi.gif"] {display:none !important;}
  .PhorumNewFlag, font.PhorumNav span[style]  { color: #00AA00 !important; font-weight: bold !important; }
  td.menuButton {background-color:#444 !important;}
  td.menuButton a {
  background-color:#444 !important;
  height: 15px !important;
  vertical-align: middle !important;}
  table.menutext {color:#000000 !important;}
  table [width="781"] [cellpadding="3"] {background-color:#999999 !important;}
  table.PhorumListTable table td {color: #eeeeee !important; background-color:#222222 !important;}
  .PhorumBodyArea, .PhorumListTable input {background-color:#999999 !important;}
  table.PhorumListTable font {color:#999999 !important; }
  table.PhorumListTable .PhorumMessage {color:#eee !important; }
  .PhorumTxtStyle {color:#999999 !important;}
  .PhorumLegendStyle table {background-color:#CCCCCC !important;}
  .boxText {color:#fff !important; border-bottom: dotted 1px #666 !important; }
  td.tableInfo {color:#eee !important; border-bottom: dotted 1px #666 !important; }
  .boxText {color:#fff !important; border-bottom: dotted 1px #666 !important; background-color: #222 !important; }
  p {color: #ccc !important; }

}
