css:
@-moz-document domain("twitter.com") {
ul.js-actions * b{display:none !important;}
li.action-open-container,
a.details b span.expand-stream-item,
a.with-icn b span.expand-stream-item,
a.js-details b span.expand-stream-item,
a.details b span.js-hide-details,
a.with-icn b span.js-hide-details,
a.js-details b span.js-hide-details{visibility:hidden !important;}


/*画像クリックプレビュー*/
.gallery-tweet
{position:relative !important;opacity: 1 !important;
background: linear-gradient(to bottom,rgba(34,113,169,0.2) 0,rgba(34,113,169,0.5) 40%,rgba(34,113,169,0.4) 80%);
  border-top: 1px solid rgba(34,113,169,0.2);


}
.swift-media-gallery
{background-color:rgba(0,0,0,0) !important;
 height:auto !important;}
.media-image,.gallery-media iframe
{margin: 0 !important;padding: 0 !important;}

/*プロフィール*/
.profile-header-inner 
{background-color: #004 !important;}

div.profile-header-inner-overlay
{background: -webkit-linear-gradient(top,rgba(0,0,0,0) 0,rgba(34,113,169,0.55) 100%) !important;}
.profile-card-inner *
{text-shadow:0px 1px 1px #000 !important;}

.bio-container
{margin:4px !important;}

p.bio,p.location-and-url
{font-size:11px !important;line-height:14px !important;
margin: auto !important;}
p.location-and-url
{
  padding: 0px !important;
position: relative !important;
top: 0px !important;
bottom: 0px !important;
margin: auto !important;}
  
/*タイムライン*/
div.tweet,div.list
{border-bottom-width: 0px !important;}

li.js-stream-item:nth-child(2n),
li.stream-item:nth-child(2n),
li.stream-item:nth-child(2n),
li.expanding-stream-item:nth-child(2n),
li.semi-expanded:nth-child(2n),
li.disco-stream-item:nth-child(2n)
{ /*background-color: rgba(244,253,255,1);*/
box-shadow:0px 2px 10px 0px rgba(0, 160, 209,0.3) inset !important;}

/*新しいツイートが○件あります*/
div.new-tweets-bar
{ background-color: #00a0d1;color: #ffffff;}

div.new-tweets-bar{transition-duration:0.3s;}


div.content-main img.avatar,
div.content-main img.js-action-profile-avatar
{}

/*時間*/
span._timestamp,
div.client-and-actions span.metadata span
{}
div.client-and-actions
{overflow: visible;}


p.tweet-text
{font-size: 22px !important;line-height: 28px !important;}
}
