css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ubuntuforums.org") {

body { background: #c5c5b6 !important; }
.page { background: #dedecf !important; color: #000 !important; }
.tborder { background :#dedecf !important; border: 1px solid #c5c5b6 !important; }
.panelsurround { background :#F8F8F3 !important; }
.panel { background :#F8F8F3 !important; }
.alt1 { background: #F8F8F3 !important; }

#masthead input[type=text] { margin-top: 30px !important; }
#masthead input[type=submit] { margin-right: 20px !important; }

.ubuntu_messagebody a:hover { padding-bottom: 1px !important; }
.ubuntu_messagebody div { margin: 5px 20px !important; }
.ubuntu_messagebody pre { width: 97% !important; }

.ubuntu_quotebackground div { margin: 5px !important; }
.ubuntu_quotebackground pre { width: 97% !important; }

.ubuntu_postbit_content { height: 100% !important; }

.ubuntu_quicksearch_content, .ubuntu_quicksearch * { background: #F8F8F3 !important; border-color: #F8F8F3 !important; }

/* Arrow up */
img[src*="/collapse_tcat.gif"], img[src*="/collapse_thead.gif"] {
width: 0 !important; height: 18px !important; padding-left: 18px !important;
background-image:url("data:image/png;base64,") !important; }

/* Arrow down */
img[src*="/collapse_tcat_collapsed.gif"], img[src*="/collapse_thead_collapsed.gif"] {
width: 0 !important; height: 18px !important; padding-left: 18px !important;
background-image:url("data:image/png;base64,") !important; }

img[src*="/reply.gif"] {
width: 0 !important; height: 20px !important; padding-left: 19px !important;
background-image:url("data:image/png;base64,") !important; }

}
