css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {


#dashboard_index,

#dashboard_inbox,

#dashboard_edit_post,

#dashboard_submissions,

#dashboard_drafts,

#dashboard_post_queue,

#dashboard_likes,

#dashboard_followers,

#dashboard_following,

#dashboard_directory,

#dashboard_lookup_contacts,

#main_doc_page,

#dashboard_settings,

#dashboard_create_tumblelog,

#dashboard_members,

#main_tag,

#main_meetups,

#main_about,

#main_explore,

#dashboard_goodies,

#main_developers,

#main_themes,

#dashboard_manage_themes,

#main_edit_theme,

#main_jobs,

#main_policy
#dashboard_tumblelog_settings {

background:#c0bcae url(http://www.pictureshack.us/images/2499wed.jpg) no-repeat bottom right fixed! important }


#dashboard_index div.l-content,

#dashboard_inbox div.l-content,

#dashboard_submissions div.l-content,

#dashboard_drafts div.l-content,

#dashboard_post_queue div.l-content,

#dashboard_likes div.l-content,

#dashboard_followers div.l-content,

#dashboard_members div.l-content,

#main_tag div.l-content,

#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: transparent !important }

.selection_nipple { border-bottom-color: #999bab !important }

.permalink { background: none !important }

.avatar_frame {

background-color: transparent !important; border-color: #999bab !important }

.notification {

background: transparent !important;

border: #999bab !important;

background-image: none !important;

margin-bottom: 1px !important }

.notification a { border-bottom: 1px solid #999bab !important }

.notification a:hover { border-bottom: 1px solid #999bab !important }

.notification_type_icon { display: none !important }

.nipple { border-right-color: #999bab !important }

.controls_section li { background: transparent !important }

.with_subtitle small { color: #999bab !important }

.recessed a { color: #999bab !important }

.radar { display: none !important }

* { text-shadow: none !important }

h1, .title { color: #999bab !important }

.no_posts_found, h1.dashboard_header { color: #999bab !important }

.dashboard_options_form {

background: rgba(0,0,0,0.1) !important;

border: none !important;

color: #999bab !important }

.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: #999bab !important }

.blue_note, .blue_note a {

background: rgba(0,0,0,0.2) !important;

color: rgba(0,0,0,0.6) !important }

#footer, #footer a {

border: none !important;

color: rgba(0,0,0,0.5) !important }

img[src*="http://static.tumblr.com/duegx7i/oKGm04vmm/000.png"] {

opacity: 0;

visibility: hidden }

.new_post {

background: #999bab url(http://static.tumblr.com/duegx7i/oKGm04vmm/000.png) no-repeat center bottom }

.new_post_label {

-webkit-text-stroke:0 !important;

color: rgba(0,0,0,0.4) !important;

opacity:1 !important;

top: 0px !important;

padding-top: 80px }

.new_post_label:hover { color: rgba(0,0,0,0.7) !important }

#pagination a {

background: rgba(0,0,0,0.4) !important;

color: #999bab !important }

#pagination a:hover {

color: #999bab !important }

#dashboard_controls a, #dashboard_controls .control {

color: #999bab !important }

.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {

display: none !important }

}
