css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photo-forum.net") {

body {
	background-color: #333333 !important;
	color:#777777 !important;
	text-align: center !important;
}
#tableHead, table[bgcolor="#E5E5E5"] {width: 943px !important;}
table#tableHead td[bgcolor="#F7F7F7"], object, td[background="http://212.36.3.59/site_design/menutail.jpg"],
 a[href="http://photo-forum.net/adrotator/ad_click.php?id=36"] {display: none !important;}
table {
	margin-right: auto !important;
	margin-left: auto !important;
}
table, tr, td, table.tableThumb {background-color: #000000 !important;}
.Link2 {color:#CC6600 !important;}
.PhorumNav {
	color: #669999 !important;
	padding-left: 4px !important;
}
font.PhorumNav a font.PhorumNav:hover {color:#66CCCC !important;}
.PhorumListHeader {
	padding-left: 10px !important;
	padding-top: 10px !important;
	padding-bottom: 10px !important;
	font-weight: bold !important;
	color: #777777 !important;
	background-color:#222 !important;
}
a, a.PhorumListRow, .PhorumSubject, .PhorumTableHeader, .boxText12 { color:#999999 !important; }
a:hover, a.PhorumListRow:hover, .PhorumSubject:hover{ color:#CCCCCC !important; }
.PhorumSubject:hover {text-decoration:none !important; }
td.PhorumListRow {
	color: #999999 !important;
	padding-left: 10px !important;
}
td.PhorumListRow[bgcolor="#ffffff"] {background-color: #222222 !important;}
img[src="http://212.36.3.59/site_design/forum_fotografia.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_obshtatema.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_tehnika.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_zanovaci.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_konkursi-izlojbi.gif"] {display:none !important;}
.PhorumNewFlag, font.PhorumNav span[style]  { color: #996600 !important; }
td.menuButton {background-color: #444 !important;}
td.menuButton a {
	background-color: #777 !important;
	color: #000 !important;
	height: 15px !important;
	vertical-align: middle !important;
}
td.menuButton a:hover {background-color: #000 !important; color: #999 !important;}
table.menutext {color:#000000 !important;}
table [width="781"] [cellpadding="3"] {background-color:#999999 !important;}
table.PhorumListTable table td, .PhorumLegendStyle table {background-color:#dddddd !important;}
.PhorumBodyArea, .PhorumListTable input {background-color:#999999 !important;}
table.PhorumListTable font {color:#999999 !important; }
table.PhorumListTable .PhorumMessage {color:#003366 !important; }
.PhorumTxtStyle {color:#999999 !important;}
.boxText, .boxText span, .boxText a, .boxTextRight, .boxTextRights {color:#999999 !important;}
.boxText font {color:#669999 !important; font-weight:bold !important;}
.boxText font:hover, .boxText a:hover {color:#336666 !important;}
a[href="http://photo-forum.net/adrotator/ad_click.php?id=203"], a[href="http://photo-forum.net/adrotator/ad_click.php?id=342"] {display: none !important;}
img[src="http://212.36.3.59/site_design/user.jpg"], img[src="http://212.36.3.59/site_design/album.jpg"] {display: none !important;}
.menuAlbum, .menuAlbum1 {color:#cccccc !important;}
.menuAlbum:hover, .menuAlbum1:hover  {color:#CCCCCC !important;}
.mainTitle {background-color:#333 !important;}
td [width="10"] {background-color:#222 !important;}
td [height="1"] {background-color:#333 !important;}
#fldComment {background-color:#999 !important; color:#000!important;}
#tableHead a img[width="200"] {display:none !important;}
.tableInfo {color:#999999 !important;}
.PhorumBodyLink {color: #006699 !important;}
.PhorumBodyLink:hover {color:#004466 !important;}
span.PhorumLegendStyleBold {color:#006699 !important;}
span.PhorumLegendStyleBold:hover {color:#004466 !important; cursor:pointer;}
}
