css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
    background: url('http://img6.imageshack.us/img6/7414/wallpaper967991.jpg') top left #000000 repeat fixed !important;

  }



ol#posts li blockquote,



body.mceContentBody blockquote {



margin-left: 0 !important;




margin-right: 0 !important;



padding-left: 10px !important;


border-width: 4px !important;



border-color: #a8bccf !important;



}



ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {




border-color: #839aaf !important;


}


ol#posts li blockquote blockquote blockquote,


body.mceContentBody blockquote blockquote blockquote {



border-color: #6b7d8f !important;


}



ol#posts li blockquote blockquote blockquote blockquote,


body.mceContentBody blockquote blockquote blockquote blockquote {



border-color: #4c5e6f !important;

}





ol#posts li blockquote blockquote blockquote blockquote blockquote,





body.mceContentBody blockquote blockquote blockquote blockquote blockquote {




border-color: #36434f !important;































}

















/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://img34.imageshack.us/img34/8323/dashicons.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}





























#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {















    background: rgba(0,60,100,0.5) !important;















  }































#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {















    background: rgba(0, 60, 100, 0.5) !important;















    -webkit-border-radius: 20px;















    -moz-border-radius: 20px;















    border-radius: 20px;















    margin: auto;















    padding: 20px 20px;















  }































h1 {















color: #666666;















text-shadow: 0 0 3px #ffffff;















}































.no_posts_found, h1.dashboard_header {















color: #666666;















}































img#content_top, img#content_bottom {















display: none;















}































#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {















    background: none;















  }































#right_column .dashboard_nav_item {















    background: none;















    background-attachment: initial;















    background-position-x: 50%;















    background-position-y: 100%;















    background-origin: initial;















    background-clip: initial;















    background-color: initial;















}































#right_column .dashboard_nav_item #hide_radar {















background-image: none;















background-repeat-x: no-repeat;















background-repeat-y: repeat;















background-attachment: initial;















background-position-x: 0%;















background-position-y: 0%;















background-origin: initial;















background-clip: initial;















background-color: transparent;















}































#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {















background: rgba(0,0,0,0.5);















}































#nav .nav_item.active {















    background: rgba(0,60,100,0.5) !important;















  }































#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {















    background: rgba(0,60,100,0.5) !important;















  }































ol#posts li.notification.alt {















    background-color: rgba(0,0,0,0.5);















}































ol#posts li.notification.first_notification {















    background-color: rgba(0,0,0,0.8);















}































ol#posts li.notification {















    background-color: rgba(0,0,0,0.8);















    border-bottom: 1px solid #000000;















}































ol#posts li.notification.last_notification {















    border-bottom: 1px solid #000000;















}































ol#posts li.post .permalink {















    background: rgba(50,0,0,0.9);















}































#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {















background: rgba(0,0,0,0.8);















border: solid #000000;















}

ol#posts li.post .post_avatar{

border-bottom: 0px solid #0A2939;

}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;


border-bottom: 1px solid #000000;


}

#right_column a#dashboard_switch_blog_menu_current:hover {

background: none;

}


#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {

background: none;

}


form.dashboard_options_form {

background-color: rgba(0,0,0,0.7);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;


border-bottom-right-radius: 10px 10px;


border-bottom-right-radius: 10px 10px;



border-top-left-radius: 10px 10px;


border-top-left-radius: 10px 10px;


border-top-right-radius: 10px 10px;


border-top-right-radius: 10px 10px;

color: #cccccc;


display: block;



font-size: 13px;


margin-bottom: 25px;



padding: 15px 22px;




}



form.dashboard_options_form .option_container {


border-top: 1px solid #000000;


}



.no_posts_found, h1.dashboard_header {


color: #666666;

}





ol#posts li.post.new_post{
width: 540px;
}
.new_post_label {
opacity: 0 !important;
}
.short_new_post .new_post_label {
opacity: 1 !important;
}
.new_post_label_icon {
display:none;
visibility:hidden;
}
.MissingE_bookmark_text{
-khtml-border-radius: 3px;
-webkit-border-radius: 3px;
}
.MissingE_bookmark_line{
display:none;
}
li.post .permalink {
-khtml-border-radius: 3px;
-webkit-border-radius: 3px;
}
#container{
width:950px;
}
#right_column{
width:220px;
}
#left_column{
width:670px;
}
}

@-moz-document url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/following"), url-prefix("http://www.tumblr.com/lookup"), url-prefix("http://www.tumblr.com/spotlight"), url-prefix("http://www.tumblr.com/help"), url-prefix("http://www.tumblr.com/docs"), url-prefix("http://www.tumblr.com/about"), url-prefix("http://www.tumblr.com/goodies") , url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/following"), url-prefix("https://www.tumblr.com/lookup"), url-prefix("https://www.tumblr.com/spotlight"), url-prefix("https://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/docs"), url-prefix("https://www.tumblr.com/about") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:none;
background-color: rgba(255, 255, 255, 1) !important;
}
#chrome_stats_counter{
background-repeat:none;
width: 844px;
}
}

@-moz-document url-prefix("http://www.tumblr.com/upload"), url-prefix('https://www.tumblr.com/upload')   {
body{
background:none !important;
background-image:none !important;
background-color: rgba(255, 255, 255, 1) !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/goodies"), url-prefix('https://www.tumblr.com/goodies')   {
#container{
width:940px;
}
}

@-moz-document url-prefix("http://www.tumblr.com/developers"), url-prefix('https://www.tumblr.com/developers')   {
#container{
width:940px;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
display:none;
}
}

@-moz-document url-prefix("http://www.tumblr.com/meetups"), url-prefix('https://www.tumblr.com/meetups')   {
#container{
width:940px;
}
#inner_content{
width:840px;
}
#footer_content{
width:900px;
}
.map_corner{
display:none;
}
}

@-moz-document url-prefix("http://www.tumblr.com/jobs"), url-prefix('https://www.tumblr.com/jobs')   {
#container{
width:900px;
}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
display:none;
}
div.l-content.second_content{
display:block;
background:none;
background-color: rgba(255, 255, 255, 1) !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/policy"), url-prefix('https://www.tumblr.com/policy')   {
#container{
width:940px;
}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
display:block;
background:none;
background-color: rgba(255, 255, 255, 1) !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/send"), url-prefix('https://www.tumblr.com/send')   {
#container{
width:735px;
}
}

@-moz-document url-prefix("http://www.tumblr.com/ask_form"), url-prefix("http://www.tumblr.com/submit_form") , url-prefix("https://www.tumblr.com/ask_form"), url-prefix("https://www.tumblr.com/submit_form") {
#container{
width:100%;
}
}
