css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.itmedia.co.jp/news/articles/"),
               url-prefix("http://www.itmedia.co.jp/anchordesk/articles/"),
               url-prefix("http://www.itmedia.co.jp/enterprise/articles/") {

body {
    padding: 0 0 0 9% !important;/* ページ全体 右寄せ */
}
#masterMain {
    border-top: none !important;
}
#masterSub {
    border-top: none !important;
    border-bottom: 1px solid #C1C1C1;/* 右カラムに下部ボーダー追加 */
    width: 290px !important;/* 1段階縮小時に右側がズレないための調整 */
}


/* ヘッダー */
#COIDP,                         /* ヘッダー上のID機能バー */
#masterNavigation,              /* ITmediaナビバー */
#masterHeader,                  /* ヘッダー,ローカルナビバー */

/* 左カラム */
#localPankuzu,                  /* パンくずリスト */
iframe,                         /* Facebook box */
#masterSocialbuttonTop,         /* 共有ボタン上 */
.endkwd,                        /* 関連キーワード */
#masterSocialbuttonBtm,         /* 共有ボタン下 */
#newzia_connect_wp_atit_rc_,    /* Ad - newziaコネクト */

/* 右カラム */
#globalHeaderSearch,            /* 検索 */
.colBoxLeftcolumnSpecialIndex,  /* 特集インデックス */
#snsContents,                   /* SNS */
#masterSub .colBoxClear,        /* 15px余白 */
.colBoxEbook,                   /* 電子ブックレット */
.colBoxMailmag,                 /* メールマガジンのお知らせ */
.colBoxSeminar,                 /* 現在受付中の主催・協力セミナー */
.colBoxITMID,                   /* アイティメディアIDからのお知らせ */
.colBoxfeedback,

/* フッター */
#masterFooter,

/* Ad ※誤爆可能性あり */
div[id^="ISA"],
div[id^="IT"] {
    display: none !important;
}
}
