css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
 * CSS: by Shelley V. Adams CC-BY 3.0 [http://creativecommons.org/licenses/by/3.0/]
 * Icons marked "Custom Silk" by Shelley V. Adams CC-BY 2.5 [http://creativecommons.org/licenses/by/2.5/] based on Silk Icon Set v1.3 by Mark James
 * BibSonomy icon by the BibSonomy project [http://www.bibsonomy.org/].
 * All other icons from Silk icon set v1.3 [http://www.famfamfam.com/lab/icons/silk/] by Mark James CC-BY 2.5 [http://creativecommons.org/licenses/by/2.5/]
 */
@-moz-document domain("www.bibsonomy.org") {
    div#container {
        left:0px !important;
        right:1px !important;
        top:0px !important;
    }
    div#header{ height: 72px !important; }
    div#logo, div#infoline {top:4px !important;}
    div#logo { font-size:32px !important; }
    div#logo, div#logoSubtitle { left:4px !important; }
    div#logoSubtitle {top:48px !important;}
    div#search {
        margin-left:0.5em !important;
        font-size:50% !important;
    }
    div#infoline { margin-right:4px !important; }
    div#language { display:none !important;}

    div#headercontainer {
        top:102px !important; /* was 125px */
        overflow:hidden;
        height:auto !important;
    }
    div#headercontainer,
    div#panelheader,
    div#bookmarksheader, div#publicationsheader,
    div#sidebarnavi, div#sidebarnavi>ul, li.sidebarnavi_entry,
    li.sidebarnavi_entry>a { height:32px !important; /* was 60px */ }
    div.listOptions { top:124px !important; /* was 160px */ }
    div.headerLogo.image {
        margin-left:20px !important;
        height:30px !important;
        width:30px !important;
    }
    div#pageOptions {
        margin-top:auto !important;
        line-height:2.1em;
        height:2.5em !important;;
    }
    #pageOptions>a.item { height:30px!important; }

    h2.header,
    h2.header.standard,
    div#panelheader, div.headerLogo.image.cv,
    div.headerLogo.image.publications, div.headerLogo.image.bookmarks,
    div#bookmarksheader.standard, div#publicationsheader.standard,
    div#sidebarnavi, div#sidebarnavi>ul, li.sidebarnavi_entry {
        padding:0px !important;
        line-height:2em !important;
        text-align:center !important;
        height:32px !important;
    }
    div#fullscreen, div#fscontent{
        clear:none !important;
        float:left !important;
    }
    div#fscontent{ width:97%; }
    div#postcontainer {
        top:132px !important;
        bottom:2px !important;
        clear:none !important;
        background-color:White !important;
    }
    div#footercontainer { height:20px !important;}
    div.footer,
    div#footercontainer { background-color:Gainsboro !important; }
    .listnav { background-color:White!important; }
    div.footer {
        height:auto!important;
        padding-top:1px !important;
        padding-bottom:1px !important;
    }
    .listnav, .listnavSelected {
        margin:0px 2px !important;
        padding:1px 2px !important;
        line-height:1em !important;
    }

    img.overlay { display:none !important;}
    a.item.edit { /* pencil */ background-image:url('data:image/png;base64,') !important; }
    span.editTags, span.ieditTags { /* tag_blue_edit */ background-image:url('data:image/png;base64,') !important;}
    li.sidebarnavi_entry_02, li.sidebarnavi_entry_02_inactive, a.item.pick, span.item.ipick { /* page_paste */ background-image:url('data:image/png;base64,') !important; }
    .post .action a.item.unpick { /*Custom Silk: clipboard_delete */  background-image:url('data:image/png;base64,') !important; }
    a.item.copy, a.item.icopy, span.item.icopy { /* page_copy */ background-image:url('data:image/png;base64,') !important; }
    a.deleteDocument {
       /* delete [minus in red circle] */ background-image:url('data:image/png;base64,') !important;
    }
    a.item.delete, span.item.idelete {
       /* bin [trash] */ /*background-image:url('data:image/png;base64,') !important;*/
        /* bomb */ background-image:url('data:image/png;base64,') !important;
    }
    li.sidebarnavi_entry_03, li.sidebarnavi_entry_03_inactive { /* [bibsonomy logo] */ background-image:url('data:image/png;base64,') !important; }
    li.sidebarnavi_entry_04, li.sidebarnavi_entry_04_inactive, div.headerLogo.image.editTags { /* tag_blue_edit */ background-image:url('data:image/png;base64,') !important; background-position:0 0; }
    li.sidebarnavi_entry_06, li.sidebarnavi_entry_06_inactive { /* feed */ background-image:url('data:image/png;base64,') !important; }
    li.sidebarnavi_entry_07, li.sidebarnavi_entry_07_inactive { /* help */ background-image:url('data:image/png;base64,') !important; }
    li.sidebarnavi_entry_05, li.sidebarnavi_entry_05_inactive, div.config {
        margin-top:0px !important;
         /* cog */ background-image:url('data:image/png;base64,') !important;
    }
    li.inbox_empty, li.sidebarnavi_entry_01_inactive { /* email */ background-image:url('data:image/png;base64,') !important; }
    #navigation2 a.signoff { /* door_out */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.inbook { /* book_open */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.misc { /* page_white */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.article { /* page_white_text */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.electronic { /* page_white_lightning */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.booklet { /* report */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.web { /* world_link */ background-image:url('data:image/png;base64,') !important; }
    .entrytype.proceedings,
    .entrytype.inproceedings { /* report_user */ background-image:url('data:image/png;base64,') !important; }
    a.item.communitypost,
    .entrytype.conference { /* group */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.incollection { /* folder_page */ background-image:url('data:image/png;base64,') !important;}
    .entrytype.patent { /* script_key */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.periodical { /* time */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.preamble { /* script_go */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.presentation { /* user_comment */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.manual,
    .entrytype.standard,
    .entrytype.techreport { /* report_key */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.unpublished { /* page_white_edit */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.preprint { /* new */ background-image:url('data:image/png !important;base64,') !important;}
    .entrytype.phdthesis,
    .entrytype.mastersthesis { /* report_magnify */ background-image:url('data:image/png;base64,') !important;}



    div.headerLogo.image.cv { /* vcard */ background-image:url('data:image/png;base64,') !important; }

    .entrytype.book,
    div.headerLogo.image.publications { /* book */ background-image:url('data:image/png;base64,') !important; }
    div.headerLogo.image.bookmarks { /* award_star_gold_1 */ background-image:url('data:image/png;base64,') !important; }

    img#fsReloadButton,
    input.fsFloatInput.reqinput+a>img { opacity:0 !important; /* These may use JavaScript. So, don't set display:none or visibility:hidden. */ }

    a#fsReloadLink,
    input.fsFloatInput.reqinput+a { /* arrow_refresh */ background-image:url('data:image/png;base64,') !important; }

    a#fsReloadLink { float:right; }
    img#fsReloadButton { float:none !important; }

    a#fsReloadLink,
    div.headerLogo.image.publications, div.headerLogo.image.bookmarks, div.headerLogo.image.editTags,
    div.headerLogo.image.cv,
    .entrytype,
    div.config,
    li.inbox_empty, li.sidebarnavi_entry_01_inactive,
    li.sidebarnavi_entry_02, li.sidebarnavi_entry_02_inactive,
    li.sidebarnavi_entry_03, li.sidebarnavi_entry_03_inactive,
    li.sidebarnavi_entry_04, li.sidebarnavi_entry_04_inactive,
    li.sidebarnavi_entry_05, li.sidebarnavi_entry_05_inactive,
    li.sidebarnavi_entry_06, li.sidebarnavi_entry_06_inactive,
    li.sidebarnavi_entry_07, li.sidebarnavi_entry_07_inactive,
    a.item.communitypost,
    a.item.copy, a.item.icopy, span.item.icopy,
    a.item.delete, span.item.idelete,
    a.deleteDocument,
    a.item.pick, span.item.ipick, a.item.unpick,
    a.item.edit,
    span.editTags, span.ieditTags,
    input.fsFloatInput.reqinput+a {
        background-position:center center !important;
        background-repeat:no-repeat !important;
        min-width:16px;
        min-height:16px;
    }

    .entrytype {
        margin-top:0px !important;
        width:16px !important;
        height:16px !important;
    }

    .post div.image {
        position:static !important;
        clear:left;
        float:left;
        padding-top:0px !important;
        margin-top:0px !important;
        margin-right:8px !important;
        width:auto !important;
        height:auto !important;
        min-height:16px !important;
        background-color:transparent !important;
    }
    .post div.metacontainer, .post div.metaContainer { margin-left:20px !important; }

    /* Fix problem with actions extending beyond backround */
    .post div.metacontainer:after, .post div.metaContainer:after {
        display:block;
        clear:both;
        content:"";
        height:0;
        overflow-y:hidden;
    }

    .post div.action {
        float:left;
        clear:left;
        position:static !important;
        margin-top:0 !important;
    }

    li.sidebarnavi_entry_01_inactive,
    li.sidebarnavi_entry_02_inactive,
    li.sidebarnavi_entry_03_inactive,
    li.sidebarnavi_entry_04_inactive,
    li.sidebarnavi_entry_05_inactive,
    li.sidebarnavi_entry_06_inactive,
    li.sidebarnavi_entry_07_inactive,
    a.item.icopy, span.item.icopy,
    span.item.idelete,
    span.item.ipick,
    span.ieditTags { opacity:0.5; }

    .post .pre_pic {
        box-shadow:none !important;
        border-color:#999 !important;
        margin-top:0 !important;
    }

    a.linkButton>img { display:none !important; }
    a.linkButton>span{ padding: 0px 0.15em 0px 1em !important; }
    a.linkButton { /* CV button */
        box-shadow:none !important;
        margin-left:0.5em;
        /* user_suit */ background-image:url('data:image/png;base64,') !important;
        background-position:2px center;
        background-repeat:no-repeat;
        background-color:#eee !important;
        border:1px solid Gainsboro;
        border-radius:3px;
    }
    
    ul.documents>li {
        list-style-image:/* attach */ url('data:image/png;base64,') !important;
    }

    ul.posts{
        padding-left:2px !important;
        padding-right:2px !important;
    }

    ul.tags.extend { max-width:85% !important; }

    div.ptags {
        margin-right:0px !important;
        margin-bottom:0 !important;
    }

    div#search>ul>li>ul { margin-top:-2px !important; }

    li.taghuge>a { padding-right:0 !important; }
    li.taghuge{ margin-right:1em; }

    form.sidebar_collapse_content {
        padding:0.5em !important;
        text-align:right;
    }

    form.sidebar_collapse_content>input[type=text] { margin-bottom:0.4em; }

    #resources { font-size:100% !important; }
    #resources * { vertical-align:top; }
    
    .tagcloud { text-align:justify !important; }
}
