    height: 40px !important;
}
.profile-card-actions, .stats a, .btn, .profile-card .location-and-url, .site-footer li, .site-footer a, a, .btn-link, .pretty-link:hover s, .pretty-link:hover b, .ns-experiment .story-title a:hover, .metadata a:hover, .account-group:hover .fullname, .stats a:hover, .stats a:hover strong, .profile-modal-header .fullname a:hover, .profile-modal-header .username a:hover, .story-article:hover .metadata, .find-friends-sources li:hover .source, .stream-item a:hover .fullname, .stream-item .time a:hover, .stream-item .view-all-supplements:hover, .tweet .details, .tweet .content-expansion a:hover, .tweet p a:hover, .tweet:hover .action-open-container a, .client-and-actions a:hover, .dismiss-promoted:hover b, .tweet .context .pretty-link:hover s, .tweet .context .pretty-link:hover b, .list .username a:hover, .list-membership-container .create-a-list, .list-membership-container .create-a-list:hover, .permalink-tweet p a, .permalink-tweet .actions a, .tweet .actions li:hover b, .open .tweet p a, .open .original-tweet .action-open-container a, .open .original-tweet .actions b, .story-header:hover .view-tweets, .card .list-details a:hover, .card .card-body:hover .attribution, .new-tweets-bar, .onebox .soccer ul.ticker a:hover, .ns-experiment .story-body-link a:hover, .remove-background-btn, .stream-item-activity-me .latest-tweet .tweet-row a:hover, .colored-actions.tweet .actions a, .colored-links.tweet p a, .mini-profile .fullname {
