css:
@-moz-document domain("tecmundo.com.br"), domain("baixaki.com.br") {
#sidebar, .fb, .tw, li[style="gp"], .w300 iframe, .partner, .list-comment .aval, #bt-respostas,
div[style=" width:970px; margin-left:auto; margin-right:auto;padding:10px 0 10px 0; height:110px; text-align:left;"], a.btn-comentar, .box-share .item, .adtopabs_h, #adtopDet, #plugins{display:none!important;}

#navbar {margin: 20px auto!important;}


#artigo-head h1 {font: normal 25px Georgia!important; color: #555!important;}
#artigo-head .subtitle {font: normal 16px arial!important;}
#artigo .subtit-artigo, #artigo h1, #artigo h2 {font: normal 19px arial!important;}

.tt {font: normal 18px arial!important; color: #555!important;}
.box-border .tt {border-bottom: 2px solid #666!important;}

#signin .item {padding:0; margin:0!important;}
#signin .item a {padding: 10px 15px; margin: -3px 5px; background-color: #00A0F0!important; color:#FFF!important; font-weight:bold!important;}
#signin .item a:hover {background-color: #00D6F0!important; text-decoration:none!important; }
#signin .item.active .btn-txt {color:#333!important; background:#FFF!important; border-bottom: 1px solid #FFF!important;}


#login-box {margin: -2px 5px!important;}
.form-link {padding: 5px!important; margin: 0 0 -2px 30px!important;}
.form-button {background: #E2E2E2!important; color: #666!important;}
.form-button:hover {background: #ccc!important; color: #555!important;}

#evento {max-height: 100%!important;}
li.coment {border:none!important;}


* {border-radius:0!important;}
#search .txt-search {color:#333!important;}
#search .txt-search:hover, #search .txt-search:focus {background-image:none!important;}
#search .txt-search:focus {background-image:none!important; border-color:#00A0F0!important;}
#search .btn-search {background-image: none!important;}

#menu {background-image: none!important;}
#menu li:hover .item {border: 1px solid #00A0F0!important; border-bottom:none!important;}
#menu .sub {border: 1px solid #00A0F0!important;}
#menu .sub a:hover {background-color: #00D6F0!important; color: #222!important;}


#box-coment-user {background:#F6F6F6!important;}
form .fm-txa {background:#FFF!important; width: 592px!important;}

.list-comment .avatar a {text-transform: capitalize!important;}
.list-comment .avatar em {font:normal 11px arial!important; opacity:0!important;}
.list-comment li:hover .avatar em {opacity:1!important;} 

#footer {background-color: #F7F7F7!important; border-top: 1px solid #DDD!important;}

#cad {border: 5px solid #B6DF23!important; background: #FFF!important;}
#cad b.t, #cad b.b {display:none!important;}


#corpoh, #contents {margin-top: 40px!important;}
#adbox1 div, #adbox2 div, #adtop div, #adsfundo div, .w300 .a300 div{display:none!important;}
#adbox1 {background:#FC0!important;}
#adbox2 {background:#CF0!important;}

#adsfundo {background: #00EE76!important; height: 50px!important; margin-top:10px!important;}

#adtop {background:#8A2BE2!important;}
.w300 .a300 {background:#EE3B3B!important;}




}
