css:
@-moz-document domain("www.tumblr.com") {
body {
 background-image: none;
 background-color: #202020;
}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
 background-color: #181818;
}
.recessed {
 background-color: #202020 !important;
}
.recessed:hover {
 background-color: #282828 !important;
}
#posts .post .post_permalink {
 background-image: url('data:image/png;base64,');
}
#posts .notification {
 background-color: #202020 !important;
}
#posts .notification.last_notification,
#posts .notification.single_notification {
 border-bottom: 1px solid rgba(0, 0, 0, .5);
}
#posts .notification .nipple {
 border-right: 6px solid #202020 !important;
}
#posts .notification .nipple.border {
 border-right: 6px solid #141414 !important;
}
div.l-header .selection_nipple {
 border-bottom: 12px solid #181818;
}
#right_column .send_fan_mail {
 background: -webkit-linear-gradient(top,rgba(48, 48, 48, 1) 0,rgba(44, 44, 44, 1) 50%,rgba(38, 38, 38, 1) 50%,rgba(36, 36, 36, 1) 100%);
 box-shadow: inset 0 1px 0 0 rgba(255,255,255,.25),0 1px 6px 0 rgba(0,0,0,.3);
}
.no_posts_found, h1.dashboard_header {
 color: #a0a0a0 !important;
}
#MissingE_preview .count {
 background-color: rgba(16, 16, 16, .75);
}
.chrome.blue {
 background: -webkit-linear-gradient(top,#808080 0,#707070 100%);
 -webkit-box-shadow: inset 0 1px 0 0 rgba(255,255,255,.2);
 border-color: #606060;
}
#return_to_top .return_to_top_icon {
 background: -650px -190px url('https://www.tumblr.com/images/dashboard_master_sprite.png?2') no-repeat transparent;
 opacity: .25;
}
#return_to_top .return_to_top_icon:hover {
 opacity: .4;
}
#auto_pagination_loader {
 background: #1b1b1b url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAABkCAIAAADITs03AAAAAXNSR0IArs4c6QAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9wDCAQNJkuWd6cAAAAdaVRYdENvbW1lbnQAAAAAAENyZWF0ZWQgd2l0aCBHSU1QZC5lBwAAACdJREFUGNNjkJCQYPr//z8TAwMDVszIyIhTjlJMK7OpbS4twwANAwCwyQQY9EHn3wAAAABJRU5ErkJggg==') top repeat-x;
}
#posts .post .post_avatar {
 background: #101010;
}
#right_column .controls_section li .blog small {
 color: #a0a0a0;
}
.tab_notice_nipple {
 background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAAAXNSR0IArs4c6QAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9wDCAQUHWGdN5sAAAAdaVRYdENvbW1lbnQAAAAAAENyZWF0ZWQgd2l0aCBHSU1QZC5lBwAAADRJREFUGFdjuGmg/B8bVlBQAGMgYGAgpACrIpgCIJsBqyJsClAU4VIAV4RPAUwRA7IjsWEA7IOM7/UbOsoAAAAASUVORK5CYII=');
}
#posts .notification a:not(.avatar_frame) {
 border-bottom: 1px solid #404040;
}
#posts .notification blockquote {
 border-left: solid 3px #606060;
}
#posts .notification .avatar_frame {
 border: 1px solid #141414;
}
#right_column .controls_section .recessed a {
 color: rgba(240,240,240,.4);
}
h1 {
 color: #404040;
}
#rescued_post > div {
 background-color: #141414 !important;
 border-bottom: 1px solid #101010 !important;
 color: #b0b0b0 !important;
}
#rescued_post > div > a {
 color: #909090 !important;
 background: none !important;
}

#pagination a {
 background-color: #141414;
}

#right_column:after {
 content: none;
}
}
