css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

url-prefix("http://twitter.com/"),

url-prefix("https://twitter.com/")

/* , url("http://twitter.com/profilenamehere/")' â† Use that if you want it to work on just a certain profile(s). Don't forget to delete the other line though, or it'll still work everywhere. */



{



#top-bar-bg {

-moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);

background: -moz-linear-gradient(center top , rgba(20,20,20,0.8), rgba(100,100,100,0.6)) !important;

background-image: (http://s3.amazonaws.com/data.tumblr.com/tumblr_m0k67s2dQP1qiot5ao1_1280.png?AWSAccessKeyId=AKIAJ6IHWSU3BX3X7X3Q&Expires=1331286458&Signature=%2B8AMd0cCpD6213gixjkv0%2Fe3Ahs%3D) !important;
}

.tweet-box h2, .stream-tabs .tab-text, .stream-tabs, ul.recommended-followers {

text-shadow: none !important;

border: none !important;

}

.tweet-box h2 {

color: #222222 !important;

}

.dashboard, .stream {

background: none !important; border: none !important; background-color: transparent !important; background-color: transparent !important;

}

.stream-item, .unselectable-stream-item  {

border-bottom: 1px solid #999999 !important;

}

.tweet-box .twitter-anywhere-tweet-box-editor, .tweet-box .proxy-editor {

-moz-box-shadow: none !important;

}

.main-content {

background: none !important;

}

.details-pane, #page-container, .user-sidebar-color, .profile-header {

background: rgba(255,255,255,0.6) !important;

}

.hovered-stream-item, .stream-item:hover, .stream .new-tweets-bar, .stream .new-tweets-bar:active, body.user-style-twttr .stream-item:hover, body.user-style-twttr .stream .new-tweets-bar, body.user-style-twttr .stream .new-tweets-bar:active, .stream-tabs .stream-tab.active .tab-text, .stream-tabs .stream-tab.active .tab-text:hover  {

background:rgba(255,255,255,0.2) !important;

text-shadow: none !important;

}

.focused-stream-item, .focused-stream-item:hover {

background: rgba(248,248,248,0.5) !important;

}

.home-header, div.pane-toolbar {

background: rgba(248,248,248,0.3) !important;

}

#search-form #search-query {

background: #FFF !important;

-moz-box-shadow: none !important;

}



#top-bar ul li a, #session #screen-name {

color: #FFF !important;

}

#global-nav .active a {

-moz-border-radius: 12px !important;

background: rgba(48,48,48,0.4) !important;

padding: 9px !important;

}

#top-bar .vr {

display:none !important;

}

/* ===== OPTIONAL STUFF, DELETE FROM CODE IF YOU WANT IT BACK ====== */

/* Who to Follow - Makes them faded/transparent */

ul.recommended-followers  {

opacity: 0.2 !important;

}

ul.recommended-followers:hover {

opacity: 1 !important;

}





}
