@-moz-document domain(msghelp.net) {
	body {
		background: #FFF !important;
		color: #2192D7 !important;
	}

	#container {
		background: url(http://i29.tinypic.com/15hk9ph.png) repeat-x !important;
	}

	#header{
		position: relative !important;
		z-index: 1 !important;
		width: 421px !important;
		height: 92px !important;
		margin-left: -25px !important;
		background: #BDE7F7 url(http://i25.tinypic.com/j13goj.png) no-repeat left top !important;
	}


	#header h1{
		margin: 10px 0 0 18px  !important;
		width: 425px !important;
		height: 92px !important;
		background: url(http://i29.tinypic.com/2d6r49u.png) no-repeat 0 0  !important;
	}

	#header h1 a{
		width: 272px !important;
		height: 70px !important;
	}

	#header #mainnav{
		display: none !important;
	}

	#contentheader{
		background-image: none !important;
		height: 0 !important;
	}

	#contentheader .r{
		background-image: none !important;
	}

	#contentheader .r .l{
		background-image: none !important;
	}

	#contentheader h2 {
		position: absolute !important;
		top: 0 !important;
		right: 0 !important;
		width: 235px !important;
		height: 92px !important;
		margin: 0 !important;
		padding: 0 !important;
		background: #BDE7F7 url(http://i31.tinypic.com/w7ejx3.png) no-repeat right top !important;
	}

	#contentheader h2 a {
		display: none  !important;
	}

	#contentheader h3 {
		position: absolute !important;
		top: 79px !important;
		right: 16px !important;
		font-size: 0px !important;
	}

	#contentheader h3 a {
		display: block !important;
		position: relative;
		z-index: 2 !important;
		float: left !important;
		line-height: 1 !important;
		width: 75px !important;
		height: 16px !important;
		font-family: Calibri, Tahoma, Verdana, sans-serif !important;
		text-transform: capitalize !important;
		font-variant: small-caps !important;
		text-align: center !important;
		font-size: 14px !important;
		color: #068 !important;
	}

	#contentheader h3 a:link, #contentheader h3 a:visited {
		background: url(http://i27.tinypic.com/yfkhf.png) no-repeat 0 0 !important;
	}

	#contentheader h3 a:hover, #contentheader h3 a:focus {
		background: url(http://i28.tinypic.com/1tnamw.png) no-repeat 0 0 !important;
		text-decoration: none !important;
	}

	#contentheaderright{
		position: absolute !important;
		top: 7px !important;
		right: 16px !important;
		width: 500px !important;
		color: #03C !important;
	}

	#contentheaderright a:link, #contentheaderright a:visited {
		color: #309 !important;
	}

	/*.tborder {
		background-color: #003366 !important;
	}

	.tcat {
		background: #666 url('#') !important;
		color: #111 !important;
	}

	.trow1 {
		color: #FFF !important;
		background: #222 !important;
	}

	.trow2 {
		color: #FFF !important;
		background: #333 !important;
	}

	.trow1 a:link, .trow1 a:visited, trow2 a:link, .trow2 a:visited{
		color: #99C !important;
		text-decoration:none !important;
	}*/

	.postd, .neutd, .negtd {
		color: #000 !important;
	}

	#footer {
		background: url(http://i25.tinypic.com/28gyvdg.png) no-repeat right 5px !important;
	}
}

