css:
/* changes the body and headlines' font */
body, #adlink{ font-family: Lucida Grande, Tahoma, sans-serif !important; }
p.title a.title{ font-family: Calibri, Segoe UI, sans-serif !important; }

/* ad */
div#ad *{ font-family: Lucida Grande !important; }
div#ad{ margin-bottom: 2px; }
div#ad img{ border-radius: 4px; margin-top: 2px; margin-bottom: 2px; }


/* sponsored link */
.link.promotedlink{
border: 1px solid rgba(0,0,0,0.25);
border-radius: 4px;
box-shadow: none;
}
.link.promotedlink.promoted{ background: rgba(255,170,0,0.2) !important; }

p.sponsored-tagline{
margin: 4px; padding: 1px 5px;
font-size: 9px;
color: white; text-transform: uppercase;
background: rgba(0,0,0,0.25);
border-radius: 100px;
cursor: default;
}

a#sr-more-link{ background: -webkit-gradient(linear,right bottom,left bottom,color-stop(0.75, #f0f0f0),color-stop(1, rgba(255,255,255,0))) !important; }


@-webkit-keyframes onLoad{
    from{ opacity: 0; }
    to{ opacity: 1; }
    }

.thing{
  -webkit-animation-name: onLoad;
   -webkit-animation-duration: 0.5s;
   -webkit-animation-iteration-count: 1;
   -webkit-animation-direction: alternate;
}

div.sidecontentbox h1{ margin-bottom: 4px; }
div.sidecontentbox ul.content{ border: 1px solid rgba(0,0,0,0.1); border-radius: 4px; }

.link{
margin-top: 1px; margin-bottom: 0px;
padding-top: 8px; padding-bottom: 8px;
box-shadow: 0px 1px rgba(0,0,0,0.05);
-webkit-transition: linear 0.1s;
}

.thumbnail{ border: 2px solid white; outline: 1px solid rgba(0,0,0,0.1); box-shadow: inset 0px 0px 1px rgba(0,0,0,0.5), 0px 1px 4px rgba(0,0,0,0.5); /* opacity: 0.9; */ -webkit-transition: linear 0.2s; }
/* .thumbnail:hover{ opacity: 1; } */

/* .over18{ background: rgba(255,0,128,0.05); box-shadow: inset 0px 0px 32px white, inset 0px 0px 32px white, 0px 1px rgba(0,0,0,0.05); } */

/* changes the color of unvisited and visited headlines */
.thing .title.loggedin.click,
.thing .title.loggedin.click:visited,
.thing .title, .thing .title.loggedin{ color: /* #05f */ #369; }
.thing .title.loggedin:hover{ text-decoration: underline; }
.thing .title:visited, .thing .title.loggedin:visited{ color: gray !important; }


/* makes the border on last visited link prettier */
.link.last-clicked{ border: none; }


/* makes the rank number look prettier */
span.rank{
width: auto !important;
margin-top: 18px !important;
padding: 1px 3px 2px;
font: 11px/1 Lucida Grande !important; font-weight: bold !important;
color: white !important;
text-align: center !important;
text-shadow: none !important;
background: rgba(0,0,0,0.2);
border-radius: 100px;
}


/* makes the score of each link more visible */
.link .score.unvoted{ color: #aaa; }


/* fixes the text fields making them look better */
#search input[type="text"]{
width: 290px;
padding: 4px 4px;
font-size: 14px;
border: 1px solid rgba(0,0,0,0.33);
border-radius: 4px;
box-shadow: inset 0px 0px 1px rgba(0,0,0,0.1);
}
#search input[type="text"]:focus{ border-color: rgba(0,0,0,0.5); outline: none; }

.usertext-edit textarea{
padding: 4px 6px 0px;
font-family: inherit;
border: 1px solid rgba(0,0,0,0.25);
border-radius: 4px;
box-shadow: inset 0px 0px 5px rgba(0,0,0,0.25);
-webkit-transition: linear 0.2s;
}
.usertext-edit textarea:focus{
border-color: rgba(0,0,0,0.4);
box-shadow: inset 0px 0px 5px rgba(0,0,0,0.1);
outline: none;
}


/* text posts will look better and prettier */
.link .usertext .md{
padding: 0px 10px;
font-family: Helvetica Neue; line-height: 1.5;
color: #404040;
background: rgba(0,0,0,0.05);
border: 1px solid rgba(0,0,0,0.25);
border-radius: 4px;
box-shadow: inset 0px 2px 8px rgba(0,0,0,0.15);
}

.link .usertext .md a:visited{ color: gray; }

p.nextprev{ text-align: center; }
p.nextprev a{
display: inline-block;
padding: 0px 8px 0px;
background: #369; color: white;
border-radius: 100px;
text-transform: uppercase;
}


/* only for RES */

@-webkit-keyframes NER{
    from{ width: 55%; background: #369; border: 1px solid transparent; border-radius: 50px; color: white; }
    to{ width: 100%; background: #F0F3FC; }
    }

#progressIndicator{
margin: 8px auto 0px !important; padding-left: 20px !important; padding-right: 20px !important;
padding-bottom: 2px !important;
width: 50% !important; height: auto !important;
font-family: Calibri !important; color: white !important;
font-size: 14px !important; font-weight: bold !important;
background: #369 !important;
border: none !important; border-radius: 50px !important;
}
#progressIndicator img{ display: none; }

.NERPageMarker{
margin: 8px auto 0px;
padding-bottom: 1px !important;
font-family: Calibri !important;
font-size: 14px !important;
   -webkit-animation-name: NER;
   -webkit-animation-duration: 1s;
   -webkit-animation-iteration-count: 1;
/*   -webkit-animation-direction: alternate; */
}
