css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tuenti.com") {


/*NUEVO DISEÑO TUENTI, "TuentiBlue 3" POR ARDOYE, 8/3/2012   v1.1 */


#header { box-shadow: 0px 0px 20px black !important; }

.fieldPost { border-radius: 10px !important; }


.userEntry .status { 
    background-color: #D8E4EA !important;
    border-radius: 10px !important; 
    box-shadow: 0px 4px 8px #aaa !important;
    }

.search { border-radius: 40px !important; }

a, span, h1, h2, h3{ font-family: tahoma !important; }

#canvas_friend_suggestions, #invitations {
    display: none !important;
    }
        
.left img {
    box-shadow: 0px 1px 30px -3px black !important;
    border-radius: 30px !important;
    border: 2px solid white !important;
    }
    
.left .body {
    margin-top: 40px;
    position: relative;
    top: 10%;
    border: none !important;
    border-bottom-left-radius: 25px !important;
    border-bottom-right-radius: 25px !important;
    }
#blog .more {
    border-bottom: 5px dotted #ddd!important;
    }

.photoAndTags {
    box-shadow: 2px 6px 5px #ddd !important;
    }

#home_main_info , .avatarMod {
    font-variant: small-caps !important;
    position: relative;
    top: -20px; right: 3.5%;
    margin-bottom: -15px !important;
    padding-top: 30px !important;
    border-right: 0.01px solid #ddd !important;
    border-bottom-left-radius: 25px !important; 
    border-bottom-right-radius: 25px !important;
    box-shadow: 0px 4px 8px #aaa !important;
    background-color: #D8e4EA !important;
    }
     

.albumDisplay img{
    width: 120px !important;
    margin: -10px !important;
    padding: 0px !important;
    }

.albumDisplay .photoInfo, #ltaAdItem, #home_unit_ads_container_id {
    display: none !important;
    background-color: white !important;
    }
    
.status span {
    color: #3c769b !important;
    font-weight: bolder !important;
    }
    
body, h3, .pagedEntry{
    background-color: #f5f5f5 !important;
    }
 
.item {
    background-color: #f5f5f5 !important
    }
    
.menu span{
    background-color:  #f5f5f5 !important;
    }


.status  .date{
    color:  grey !important;
    font-weight: normal !important;
    }

.body .views, .body strong{
    color: #333 !important;
    }
    

.homeItem .body, .homeItem li , .avatarMod .body {
    background-color: #D8e4EA !important;
    }

.avatarMod .body {
    position: relative;
    right: 0px;
    }

.homeItem ul{
    border-top: 4px dotted #66aa66 !important;
    }
   
#new_messages_indication_header {
    background-image: none !important;
    color: white !important;
    border-radius: 50px !important;
    }
    
#home_user_name , .views, #home_main_info .itemBody{
    color: #3c769b !important;
    }
    
.item .video, .richMedia, .molaSentence {
    background-color: transparent !important;
    }

#char_roster_list_container {
    border-bottom-left-radius: 25px !important;
    border-bottom-right-radius: 25px !important;
    }

.peopleStack img, .borderFit {
    border-radius: 25px !important;
    }

.userEntry  img{
    border-radius: 60px !important;
    }
    

.itemBody img /*.externalImage*/ {
    border-radius: 0px !important;
    }

#wall_posts_content img, #wall_posts_content .byAvatar, .avatarFix {
    border-radius: 60px !important;
    }
    
#avatar img {
    position: relative;
    right: 3px;
    }
    
#tag_entries li, #chat_roster_module, #home h2, #chat_home_roster, .chatStatus, .item .actions {
    background-color: #f5f5f5 !important;
    }

.left .action {
    background-color: transparent !important;
    position: relative;
    left: 35px;
    }
.statusWall {
    background-color: #D8e4EA !important;
    }

.mainPoints h3 {
    background-color: transparent !important;
    }
    
.left .action{
    padding-right: 40px !important;
    }
#home_main_info ul {
    padding-left: 20px !important;
    }
.video img {
    border-radius: 0px !important;
    }
.blogEntry .status {
    background-color: #dcdcdc !important;
    }
#avatar, #home_main_info {
    margin-bottom: 2em !important; 
    }
    
    
}
