css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {

/* New Tab Page Styling for Fx 13+ : modified from rob64rock's style */

#newtab-scrollbox {background:#222222 url("http://i25.photobucket.com/albums/c70/MDLeers/pic_zps23b1877c.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;}

#launcher, #newtab-margin-top, .newtab-side-margin, #newtab-margin-bottom, #newtab-search-container, #newtab-margin-undo-container {display:none!important;}

#newtab-horizontal-margin {margin-left:10px!important; padding:15px!important;}

#newtab-horizontal-margin > div:first-child {
-moz-margin-end: -10px !important;
}

#newtab-grid {
margin:0 auto!important;
background:none!important;
background-color:transparent!important;
border:none!important;
box-shadow:none!important;
}

#newtab-grid .newtab-row {
margin-top: 5px!important;
margin-bottom: 25px!important;
}

.newtab-cell {
background:none!important;
background-color:transparent!important;
}

.newtab-site, .newtab-thumbnail, .newtab-cell {
border-radius: 8px!important; border:transparent!important;
}

.newtab-title {background:#222222 url("http://i25.photobucket.com/albums/c70/MDLeers/pic_zps23b1877c.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;
text-shadow: -2px 0 2px white, 0 2px 2px white, 2px 0 2px white, 0 -2px 2px white!important; -moz-transition-property:text-shadow, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:ease-in-out!important;
font-weight: bold!important;
font-size:1.2em!important;
border-radius: 0px 0px 6px 6px!important;
text-align:center!important;
margin-bottom: -5px!important;
padding-bottom: 0px !important;
}

.newtab-title:before {
content:"â‡‘"!important; font-size:1.5em!important; text-shadow: -8px 0 8px red, 0 8px 8px red, 8px 0 8px red, 0 -8px 8px red!important; -moz-transition-property:text-shadow, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:ease-in-out!important; color:white; float:left!important; padding-right:15px!important;
}

.newtab-site:hover span.newtab-title:before {
text-shadow: -8px 0 8px green, 0 8px 8px green, 8px 0 8px green, 0 -8px 8px green!important;
}

.newtab-cell:hover .newtab-title {
color: white !important;
background:#222222 url("http://i25.photobucket.com/albums/c70/MDLeers/pic_zps23b1877c.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;
border:none!important;
box-shadow:none!important;
text-shadow: -2px 0 2px black, 0 2px 2px black, 2px 0 2px black, 0 -2px 2px black!important;
}

.newtab-cell:hover .newtab-thumbnail {
box-shadow:inset -1px 1px 3px 3px rgba(0, 0, 0, 20), -1px 1px 2px 2px rgba(255, 255, 255, 254)!important;
}

input[pinned="true"] {
box-shadow:inset 0.05em 0.05em 0.2em red, inset -0.05em -0.05em 0.2em red, 0.05em 0.05em 0.2em red!important;
}

.newtab-thumbnail[style*="config"] {
background-image: url(data:image/jpeg;base64,)!important; /* -moz-linear-gradient(top,rgba(255,255,255,.2), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 80%, rgba(255,255,255,.2)), -moz-linear-gradient(left, rgba(255,255,255,.2), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 80%, rgba(255,255,255,.2))!important; outline-color: rgba(255,255,255,.6) !important; */
}

/* ------------------------------------------------------------------------------------------------------------ */


}
