css:
/* AGENT_SHEET */

/* SCROLLBAR */

scrollbar thumb:not(:hover)
{ opacity: 0.8 !important;
width:11px !important;
}

scrollbar thumb[orient="vertical"], 
textbox scrollbar thumb[orient="vertical"]
{-moz-appearance: none !important;
border-radius: 12px !important; 
border:2px solid #FFF !important; 

box-shadow: 0px 2px 14px 4px rgba(0,0,0,0.5), inset 0px 0px 6px rgba(0,0,0,.5) !important;  
background: #676767 !important; 
}

scrollbar thumb[orient="horizontal"],
textbox scrollbar thumb[orient="horiztonal"] {
height: 12px !important;
-moz-appearance: none !important;
border-radius: 12px !important; 
border:2px solid #FFF !important; 
box-shadow: 0px 2px 14px 4px rgba(0,0,0,0.5), inset 0px 0px 6px rgba(0,0,0,.5) !important;  
background: #676767 !important; 
}

scrollbar>slider {
background: #ECECEC !important;
border-radius: 12px;
box-shadow: inset 0px 1px 5px rgba(0,0,0,0.4);
}

scrollbar thumb:active, scrollbar thumb:focus, 
textbox scrollbar thumb[orient="vertical"]:active,
textbox scrollbar thumb[orient="vertical"]:focus{
/* box-shadow: 0px 1px 8px 3px #000 !important; */
box-shadow: 0px 1px 8px 3px rgba(0,0,0,0.8), inset 0 0 4px #0B39A3 !important;
background: #0088FF !important; 
}


/* hide scrollbar up/down buttons */
scrollbar scrollbarbutton {visibility: collapse !important;}



