css:
@-moz-document domain("wikipedia.org") {
body {
overflow-x: hidden;
}
a.new {
color: #BBBBBB !important;
}
a.new:visited {
color: #C0C0C0 !important;
}
a:visited {
color: #003366 !important;
}

#mw-page-base,#p-personal,#left-navigation,#p-views,#siteNotice,.metadata {
display: none;
}

#content {
border-color: #CCC !important;
}

#right-navigation {
margin-top: 1px !important;
}

div#simpleSearch
button#searchButton {
padding-top: 0.2em !important;
}

#p-search {
margin-right: 16px;
}

div#mw-panel {
position: fixed !important;
overflow: auto;
top:0 !important;
max-height:98%;
}

div#footer {
background:white !important;
}

body {
font-family: "Segoe UI" !important;
}

table {
border: none;
}

.toc {
border: 1px solid #CCC;
}

.infobox {
border: 1px solid #CCC !important;
}

.thumbinner {
border: none !important;

}

#simpleSearch {
background: #F5F5F5 !important;
border: 1px solid #CCC !important;
}

caption {
margin: 3px 0;
}

#searchInput {
font-family: "Segoe UI";
margin-left: 2px;
}

.metadata {
right: -27px !important;
top: -44px !important;
z-index: 5;
opacity: 0.5;
-webkit-transition: all 0.2s ease-in-out;
}

.metadata:hover {
right: -16px !important;
opacity: 1;
}


.vectorMenu {
display: none;
}
}

@-moz-document domain("wikibooks.org") {
a.new {
color: #BBBBBB !important;
}
a.new:visited {
color: #C0C0C0 !important;
}
a:visited {
color: #003366 !important;
}

#mw-page-base,#p-personal,#left-navigation,#p-views,#siteNotice,.metadata {
display: none;
}

#right-navigation {
margin-top: 1px !important;
}

div#simpleSearch
button#searchButton {
padding-top: 0.2em !important;
}

#p-search {
margin-right: 16px;
}

div#mw-panel {
position: fixed !important;
overflow: auto;
top:0 !important;
max-height:98%;
}

div#content {
border: 1px solid #CCC !important;
background:white !important;
border-color: #CCC !important;
}

.mw-body#content {
border: 1px solid #CCC !important;
border-color: #CCC !important;
}


div#footer {
background:white !important;
}

body {
font-family: "Segoe UI" !important;
}

table {
border: none !important;

}

.thumbinner {
border: none !important;

}

#simpleSearch {
background: #F5F5F5 !important;
border: none !important;
}

caption {
margin: 3px 0;
}

#searchInput {
font-family: "Segoe UI";
margin-left: 2px;
}

.metadata {
right: -27px !important;
top: -44px !important;
z-index: 5;
opacity: 0.5;
-webkit-transition: all 0.2s ease-in-out;
}

.metadata:hover {
right: -16px !important;
opacity: 1;
}


.vectorMenu {
display: none;
}

#content,#footer {
  border-left: 1px #CCC solid;
}
#simpleSearch {
  border: 1px #CCC solid !important;
}
body .ui-button.ui-button-blue {
    background: #555 !important;
    border-radius: 0 !important;
    border: 1px #222 solid !important;
}
.ui-button.ui-button-blue.ui-button-large:hover {
    background: #555 !important;
}
body .ui-button.ui-button-blue:hover {
    background: #777 !important;
    border: 1px solid #555 !important;
}
}
