css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("livejournal.com") {



ul.b-pager-pages, ul.b-pager-pages a {
font-size: 100% !important;
background-image: none !important;
background-color: #dddddd !important;
color: #333333 !important;
}

.b-pager-page-active { 
border-radius: 5px 5px 5px 5px;
box-shadow: none;
background: #000000 !important;
color: #ffffff !important;
}

.b-pager, .b-xylem {
background-image: none !important;
background-color: #ffffff !important;
margin-top: 0px !important;
font-size: 100% !important;
}

.b-addcomment {
font-size: 100% !important;
color: #777777 !important;
height: 0px !important;
}

.b-addcomment-icon {
margin-top: 8px !important;
}

.comment-wrap .odd {
background-color: #dddddd !important;
}

.comment-head.even {
background-color: #dddddd !important;
}

.b-watering-wrapper {
padding: 35px 10px 10px 10px !important;
}

.b-pager-pages {
max-width: 700px !important;
}

.b-xylem {
padding: 2px 0 15px !important;
}

.b-leaf-header, .b-massaction, .b-watering-wrapper {
background-image: none !important;
background-color: #dddddd !important;
}

.b-leaf-header .b-leaf-actions, .b-leaf-controls {
visibility: visible !important;
color: #777777 !important;
}

.b-leaf-article, .b-leaf-username, .b-leaf-createdtime {
font-size: 100% !important;
color: #4D4D4D !important;
}

 .b-leaf-controls {
 float: right  !important;
 margin: 0px !important;
 }
 
 .b-leaf-cheader {
 background: #c6c6c6 !important; }

.b-massaction {
background-image: none !important;
}

.b-ljbutton-submit {
padding-top: 0px !important;
margin: -10px 0px -5px -7px !important;
background-color: #dddddd !important;
background-image: none !important;
}

.b-watering-bar {
padding-top: 0px !important;
height: 20px !important;
background-image: none !important;
background-color: #dddddd !important;
font-size: 100% !important;
}

.b-watering-buttons {
height: 30px !important;
border: 0px !important;
}

.b-watering-wrapper {
margin: 0px !important;
padding-bottom: 0px !important;
}

.b-watering-userpic {
background-color: #ddddd !important;
border: 0px !important;
}

.b-watering-arrows {
display:none !important;
}

h1 {
font-size: 100% !important;
}

td.b-singlepost-author-userinfo {
font-size: 100% !important;
}

span.b-singlepost-author-date { 
font-size: 100% !important;
}

.b-singlepost {
font-size: 100% !important;
}

table.container {
background-color: #dddddd !important;
background-image: none !important;
}

.standout {
height: 5px !important;
}

.b-xylem-first {
border-top: 2px solid rgb(119, 119, 119) !important; 
background: -moz-linear-gradient(center top , rgb(189, 189, 189) 0%, rgb(255, 255, 255) 100%) repeat scroll 0% 0% rgb(255, 255, 255)
}

A:link {
color: #4D4D4D !important;
text-decoration: underline;
}

A:visited {
color: #A0A0A0 !important;
text-decoration: underline;
}
label.b-leaf-actions-label { 
color: #646464 !important;
font-size: 100% !important;
}

a {
font-size: 100% !important;
}


}
