css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* TABSONTOP = TRUE */

tabs[tabsontop="true"]
{
    margin-left: -4px !important;
}

/* maximerad knapp */
[sizemode=maximized][tabsontop="true"] #appmenu-button 
{
    border-radius: 3px 3px 0px 0px !important;
    padding: 0px 5px 0px 3px !important;
    margin-top: 1px !important;
    margin-left: 1px !important;
    margin-bottom: -8px !important;
    height: 26px !important;
    min-width: 45px !important;
    border: 1px solid #818283 !important;
    border-bottom: 0px !important;
}


/* nödvändigt för att markera knapp i alla lägen - flytta upp toolbaren över knapp, ej knapp ned över toolbar*/
window[sizemode="normal"][tabsontop="true"] #navigator-toolbox[tabsontop="true"] #TabsToolbar
{
	margin-top: -30px !important;
	margin-left: 48px !important;
}

/* knappstyle normal */
window[sizemode=normal][tabsontop="true"] #appmenu-button
{
	height: 65px !important;
	width: 45px !important;	
	
    /* ny storlek  */
	min-width: 45px  !important;
    padding: 2px 5px 39px 3px !important;

    /* ny position */
    margin-bottom: -29px !important; /* specialfix  för markeringsevne */
    margin-top: 20px !important;
    margin-left: 3px !important;
    
    /* avrundade hörn */
    border-radius: 3px 3px 0px 0px !important;
    
    /* kant färger */
    border: 1px solid #8e979f !important;
    border-top: 1px solid #818283 !important;
    border-left: 1px solid #818283 !important;
    border-right: 1px solid #818283 !important;
    border-bottom: 4px double #818283 !important;
}




/* fix att menypopup kommer för långt ned */
window[sizemode=normal][tabsontop="true"] #appmenu-button menupopup
{
    margin-top: -42px !important;
}





/* med icon, utan text - tack till movable firefox icon */
#main-window #appmenu-button > .button-box .button-icon 
{ 
	list-style-image: url("chrome://branding/content/icon16.png") !important;
}
#main-window[privatebrowsingmode=temporary] #appmenu-button .button-icon 
{
    list-style-image: url(chrome://browser/skin/Privacy-16.png) !important;
}
#appmenu-button > .button-box .button-text 
{
	display: none;
}

#appmenu-toolbar-button > dropmarker,
#appmenu-button > .button-box dropmarker 
{
    /* display: none !important; */
}




/* fix menubar, så den inte ligger över andra knappar */
window[sizemode=maximized] #main-menubar
{
    margin: 1px !important;
}
window[sizemode=normal] #main-menubar
{
    margin-bottom: 35px !important;
}




/*[[knappfarg]]*/

style_setting_options.value:
/* bakgrunds färger*/
#main-window #appmenu-button
{
     /* standard */    
    background-image: -moz-linear-gradient(bottom, rgba(10%,10%,10%,.4) 1px, transparent 1px), 
                      -moz-linear-gradient(transparent, hsla(0,0%,45%,.1) 1px, hsla(0,0%,32%,.2) 80%, hsla(0,0%,0%,.2)),
                      -moz-linear-gradient(hsl(214,44%,87%), hsl(214,44%,87%)) !important;

}

#main-window #appmenu-button[open],
#main-window #appmenu-button:hover, 
#main-window #appmenu-button:active
{

    /* standard */

    background-image: -moz-linear-gradient(bottom, rgba(10%,10%,10%,.4) 1px, transparent 1px), 
                      -moz-linear-gradient(hsla(0,0%,100%,.3) 1px, hsla(0,0%,75%,.2) 80%, hsla(0,0%,60%,.2)),
                      -moz-linear-gradient(hsl(214,44%,87%), hsl(214,44%,87%)) !important;


                         
}


[tabsontop="true"] #appmenu-button[open],
[tabsontop="true"] #appmenu-button:hover, 
[tabsontop="true"] #appmenu-button:active
{   
    box-shadow: 0px 0px 0px #999 !important;


}


[tabsontop="false"] #appmenu-button[open],
[tabsontop="false"] #appmenu-button:hover, 
[tabsontop="false"] #appmenu-button:active
{   

    /* behövs inte då det är en förutsättning för bakgrund och redan satt */

}

