css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.wakachan.org/unyl/") {

html, body {
        font-size:inherit !important;
        background:#EEFFEE !important;
        color:#000 !important;
}

a {
        color:#345C34 !important;
        font-family:inherit !important;
        text-decoration:underline !important;
}

a:hover {
        color:#117743 !important;
}

.rules {
        font-family:inherit !important;
        font-size:inherit !important;
        font-size:0.7em !important;
}

.filesize {
        font-family:inherit !important;
        font-size:inherit !important;
	text-decoration:none !important;
        padding-left:inherit !important;
}

.postername, .commentpostername {
        font-family:inherit !important;
        font-size:inherit !important;
}

.omittedposts {
        font-family:inherit !important;
        font-size:inherit !important;
}

.reflink a { 
        color: #000 !important;  
}

.reflink a:hover {
        font-weight:bold !important;
}

.postblock, .reply {
        background:#DAF0D6 !important;
        color:#000 !important;
}

.unkfunc {
	background:inherit !important;
	color:#117743 !important;
}

.highlight {
        background: #88DD88 !important;
        color:#000 !important;
        border: none !important;
        width: inherit !important;
}

.reply {
        width: inherit !important;
}

.reportabuse {
        color:#345C34 !important;
}

.wakachong-spoiler { 
        background: #000 !important; 
        color: #000 !important; 
} 

.wakachong-spoiler:hover { 
        color: #FFF !important; 
}

}
