css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("frndc.com") {

body {
  background-image: none !important;
  background-color: #f1f1f1 !important;}

a.nav-link {
  color: gray !important;}
a.on {
  color: #000000 !important;
  border-color: #F1F1F1 !important;
  background-color: #FED63D !important;}
a.off {
  color: #000000 !important;
  border-color: #F1F1F1 !important;}
code {
  color: #000000 !important;
  border: 1px solid #F8F8F8 !important;
  background-color: #ffffff !important;}

.language {
  background-image: url(https://lh6.googleusercontent.com/-ZT8k-uTSwDg/T2Ek0PRYK8I/AAAAAAAABTg/Oiq9BgFEQFg/s26/glyphicons_266_flag.png) !important;
  background-repeat: no-repeat !important;
  background-position: top !important;
  width: 25px !important;
  height: 25px !important;}
nav {
  border-bottom: #ffffff !important;}
a#nav-login-link.nav-login-link:hover, a#nav-register-link.nav-commlink:hover {
  background-color: #FED63D !important;
  border-color: #FED63D  !important;
  text-decoration: none !important;}
.nav-commlink, .nav-login-link{
  color: #000000 !important;}
.nav-commlink.selected {
  border-bottom: #ffffff !important;}
.nav-ajax-left {
  font-size: small !important;
  font-weight: bold !important;
  background-color: #FED63D !important;
  border-radius: 3px !important;}
a#nav-network-link.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh5.googleusercontent.com/-oRlGBLRtqw8/T2Ee-TiippI/AAAAAAAAA6I/QNocEPZ2Wgw/s35/glyphicons_043_group.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-network-link.nav-commlink:hover {
  background-color: #FED63D !important;}
a#nav-network-link.nav-commlink.selected {
  background-color: #ffffff !important;}
a#nav-home-link.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh3.googleusercontent.com/-vSJrXIOwhAw/T2EeSeok-NI/AAAAAAAAA4M/KQTrgQ6EM4o/s27/glyphicons_020_home.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-home-link.nav-commlink:hover {
  background-color: #FED63D !important;}
a#nav-home-link.nav-commlink.selected {
  background-color: #ffffff !important;}
a#nav-community-link.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh4.googleusercontent.com/-gjWLoa_bekU/T2EeShI00vI/AAAAAAAAA4Y/GW4pzDW-ACM/s32/glyphicons_024_parents.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-community-link.nav-commlink:hover {
  background-color: #FED63D !important;}
a#nav-community-link.nav-commlink.selected {
  background-color: #ffffff !important;}
a#nav-notify-link.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh3.googleusercontent.com/-Pjr8yutncJU/T2EePdtof6I/AAAAAAAAA20/B1Yzpdv2Zgk/s28/glyphicons_006_user_add.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-notify-link.nav-commlink:hover {
  background-color: #FED63D !important;}
a#nav-notify-link.nav-commlink.selected {
  background-color: #ffffff !important;}
a#nav-messages-link.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh5.googleusercontent.com/-BXP9IJYhZlk/T2EeQdBybKI/AAAAAAAAA3U/YLbJLimArIE/s24/glyphicons_010_envelope.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-messages-link.nav-commlink:hover {
  background-color: #FED63D !important;}
a#nav-messages-link.nav-commlink.selected {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #ffffff !important;
  background-image: url(https://lh5.googleusercontent.com/-hVGKMOfSxTw/T2Ego5eQwhI/AAAAAAAABAc/au8SUig15VU/s28/glyphicons_120_message_full.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  text-decoration: none !important;}
a#nav-notifications-linkmenu.nav-commlink {
  width: 20px !important;
  display: block !important;
  text-indent: -300px !important;
  overflow: hidden !important;
  background-color: #f1f1f1 !important;
  background-image: url(https://lh3.googleusercontent.com/-xRjIkf_pQZY/T2EetWawZMI/AAAAAAAAA5Y/unLIePYLtWU/s26/glyphicons_032_wifi_alt.png) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important;
  border: none !important;
  text-decoration: none !important;}
a#nav-notifications-linkmenu.nav-commlink:hover {
  background-color: #FED63D !important;}

aside {
  background-image: none !important;}
.widget {
  border-color: #F8F8F8 !important;}

#sidebar-group-list a {
  color: #000000 !important;}  
.sidebar-group-element.group-selected {
  background-color: #FED63D !important;
  border: none !important;}
  
.nets-link.nets-selected {
  color: #000000 !important;
  background-color: #FED63D !important;
  border: none !important;}
ul.nets-ul li a {
  color: #000000 !important;}
.sidebar-group-element:hover .group-edit-icon.small-pencil, li.sidebar-group-li .pencil:hover, .group-edit-icon.small-pencil:hover {
  background-image: url(https://lh4.googleusercontent.com/-mttfFQlgxTE/T2E90cXBToI/AAAAAAAABb4/YhPrDtOBRKM/s13/glyphicons_030_pencil.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important}
a.profile-edit-side-link.icon.edit {
  background-image: url(https://lh4.googleusercontent.com/-mttfFQlgxTE/T2E90cXBToI/AAAAAAAABb4/YhPrDtOBRKM/s13/glyphicons_030_pencil.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: bottom !important}

section {
  background: #ffffff !important;}
  
.tabs {
  background-image: none !important;
  background-repeat: no-repeat !important;
  background-color: #F8F8F8 !important;
  border-radius: 3px 3px 3px 3px !important;
  border-bottom: 0px !important;}
a.tab.button {
  color: #000000 !important;}
a.tab.button.active {
  color: #000000 !important;
  background-color: #FED63D !important;
  border-radius: 3px 3px 3px 3px !important;}
a.tab.button.active:hover {
  text-decoration: none !important;}
  
#jot-title {
  width: 480px !important;}

.wall-item-content-wrapper, .wall-item-content-wrapper.shiny {
  border-color: #F8F8F8 !important;
  background-image: none !important;} 
  
.wall-item-tools {
  background-image: none !important;
  background-color: #F8F8F8 !important;}
.pencil {
  background-image: url(https://lh4.googleusercontent.com/-mttfFQlgxTE/T2E90cXBToI/AAAAAAAABb4/YhPrDtOBRKM/s13/glyphicons_030_pencil.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;}
.like {
  background-image: url(https://lh4.googleusercontent.com/-a_hdACXfgiM/T2GgE3B7fKI/AAAAAAAABeA/_TdEysDWSos/s13/glyphicons_193_circle_ok.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;}
.dislike {
  background-image: url(https://lh4.googleusercontent.com/-GT3jrBx3sDA/T2GhpnB8KqI/AAAAAAAABeM/ggvBIuKrrRQ/s13/glyphicons_191_circle_minus.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;}
.recycle {
  background-image: url(https://lh4.googleusercontent.com/-m-W8hKHmgAk/T2GVMLHWaAI/AAAAAAAABcw/cCeZHf0Y_Yw/s15/glyphicons_080_retweet.jpg) !important;
  background-position: right center !important;
  background-repeat: no-repeat !important;}
.remote-link {
  background-image: url(https://lh3.googleusercontent.com/-TaV2q_4AAtk/T2GaifbcsNI/AAAAAAAABdc/xWPYZkZg8kc/s13/glyphicons_152_new_window.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: left center !important;}
.lockview {
  background-image: url(https://lh5.googleusercontent.com/-_4ZpmpeCeRI/T2GiOrXllwI/AAAAAAAABeY/HHGUSp88C78/s14/glyphicons_203_lock.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: left center !important;}
.wall-item-content-wrapper.comment .remote-link, .lockview {
  opacity: 0.2 !important;}
.star-item.icon.unstarred {
  background-image: url(https://lh5.googleusercontent.com/-vojg4-aOD0g/T2GeYd6a2HI/AAAAAAAABdo/jJYGwV13gfo/s13/glyphicons_048_dislikes.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;}
.tag-item.icon.tagged {
  background-image: url(https://lh6.googleusercontent.com/-YqX1zAS7UNw/T2Ge2J_wZBI/AAAAAAAABd0/U2h8RvOMQtA/s15/glyphicons_066_tags.jpg) !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;}
  
.wall-item-content-wrapper.comment {
  background-color: #F8F8F8 !important;}
.comment-edit-form {
  background-color: #F8F8F8 !important;}

.defaultSkin iframe {
  background: #ffffff !important;}
iframe#profile-jot-text_ifr, #profile-jot-text_ifr html {
  background-color: #ffffff !important;}

span.pager_first, span.pager_n, span.pager_last, span.pager_next {
  font-size: medium !important;
  border-color: #F1F1F1 !important;
  background-color: #F1F1F1 !important;}
span.pager_first:hover, span.pager_n:hover, span.pager_last:hover, span.pager_next:hover {
  text-decoration: underline !important;}
span.pager_current {
  font-size: medium !important;
  border-color: #FED63D !important;
  background-color: #FED63D !important;}

}
