css:
@-moz-document domain('diskusjon.no') {
ul.post_controls {
  margin: 0!important; }

#leftCol {
  border: 2px solid #9e9eb6!important;
  border-radius: 4px!important;
  padding: 3px!important;
  background-color: #bbbbcc!important; }

.ipsBox {
  background-color: #aaaaaa!important;
  border-radius: 5px!important;
  padding: 5px!important;
  margin-top: 5px!important; }

.post_body {
  border-left: 1px solid #aaaaaa!important;
  padding-left: 9px!important;
  margin-left: 145px!important; }

.post_block h3 {
  background: #c3c3c3!important; }

.feedBox {
  background: #dddddd!important; }

#forumList li div {
  background-color: #dddddd!important; }

.post_body .post {
  font-size: 13px!important;
  line-height: 1.3!important; }

.entry-content {
  color: #111!important; }

.pagination .pages a, .pagejump {
  color: #777!important; }

td.col_f_content ul.mini_pagination li a {
  color: #000 !important!important; }

table.ipb_table .mini_pagination {
  opacity: 0.6!important; }

div.blockquote {
  border-right: 2px solid #D1D1D1!important;
  border-bottom: 2px solid #BBB!important;
  margin-bottom: 1em!important; }

.signature {
  padding: 4px!important; }
  .signature:hover {
    border: 1px solid #ccc!important;
    padding: 3px!important;
    padding-top: 4px!important;
    color: #333!important;
    background-color: #EEE!important; }
    .signature:hover .hide_signature {
      background: url("http://www.diskusjon.no/public/style_images/master/cross.png") !important!important; }

.hide_signature {
  right: 7px !important!important; }

body {
  background: #aaaaaa!important; }

#topAds {
  display: none!important; }

.ipsBox_container {
  background: #dddddd!important;
  border: none!important; }

.post_block {
  background-color: #e5e5e5!important;
  border-bottom: 1px solid #aaaaaa!important; }

.main_width, #content {
  width: 1020px !important!important; }

#content {
  background-color: #eee!important;
  padding: 7px!important; }

#forumTopStories {
  display: none!important; }

.unread {
  background-color: #eef!important; }

.unread .altrow, .unread.altrow {
  background-color: #ddf!important; }
}
