css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document domain("zillow.com") ,url-prefix(http://www.zillow){ 
/* =ZILLOW NOTIFICATIONS */
.uss-count, 
.menu-nav .uss-count {
    margin: -2px 1px 0 7px;
    padding: 0 6px;
    line-height: 15px;
    text-align: center;
    color: white;
    font-weight: bold;
    font-size: 11px;
    background: -webkit-linear-gradient(top, #C63136, #A02528);
    ;
    border: 1px solid #A02528;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    display: block;
    float: right;
    text-shadow: none;
/*    box-shadow: 0px 1px 1px #AAA; */
}

.saved-search-row .edit-search {
    opacity: 0;
}

#home #notification-component .uss-count {
    border: 0;
    background: none;
    font-weight: normal;
    color: #A02528;
    font-size: 1.154em;
    margin: 0 8px 0 0;
    padding: 0;
    line-height: 21px;
}

/* ACTIONBAR NO ICONS */
.action-bar .menu-label a {
    padding-left: 10px !important;
}

.ztsa a, .ztsi .icon {
    background: none;
}

.action-bar .menu-item .menu-label.saved-search a {
    padding: 0;
}

.ztsi .facebook {
    display: none;
}

#show-hide-filters a#save-filters, #show-hide-filters #save-filters a {
    padding: 0;
}

/* BUBBLE MODIFICATION */
#bubble-map-container .map-bubble, #inner-map-container .map-bubble {
    -moz-border-radius: 10px;
    -webkit-border-radius: 4px;
    border-radius: 0px;
    -moz-box-shadow: 10px 7px 5px rgba(68,68,68,0.6);
    -webkit-box-shadow: 10px 7px 5px rgba(68,68,68,0.6);
    box-shadow: 0px 1px 7px rgba(30,30,30,1);
}

#bubble-map-container .search-result, #inner-map-container .search-result {
    background-color: white;
    border: 0;
    -moz-border-radius: 10px;
    -webkit-border-radius: 4px;
    border-radius: 0px;
}

/* LIST CHANGE */
.search-result .property-info {
    line-height: 1.4;
}

.search-result, .search-result li {
    font-size: 13px;
}

.search-result .zestimate {
    height: auto;
}

#property-home-info[style] {
    min-width: 370px !important;
}

span.definition {
    border-bottom: 0;
    background: url(data:image/gif;
    base64,R0lGODlhAgABAIAAAKqqqv///yH5BAAHAP8ALAAAAAACAAEAAAICRAoAOw==) repeat-x bottom;
}

.search-result .actions {
    display: none;
}

.search-result .value-info {
    max-width: 200px;
}

#list-container .search-result {
    cursor: default;
}

#bubble-map-container .map-bubble a.close, #inner-map-container .map-bubble a.close {
    top: 10px;
    bottom: auto;
}

#list-container .search-result .value-info {
    max-width: 215px;
    width: 215px;
}

.search-result .photo a {
    width: 124px;
    height: 94px;
}

.search-result .photo a img {
    height: 92px;
}

.search-result .photo-num a {
    width: auto;
    height: auto;
    text-transform: lowercase;
}

.search-result .photo-num {
    background: rgba(0,0,0,0.7);
    padding: 4px;
}

.search-result .photo .dcarat {
    display: none;
}

#searchActionBar {
    background: #F9F9F9 !important;
}

#property-sort-control .sort-by {
    color: #555;
}

.generic-box .module-head {
    background: #FFF !important;
    height: auto;
    line-height: 1em;
    font-size: 15px;
    font-weight: bold;
    padding: 10px 0 0 10px !important;
    color: #333;
    text-transform: capitalize;
}

.generic-box .module-head {
    border-radius: 0 !important;
}

.generic-box .module-head h2, .generic-box .module-head h5 {
    line-height: 1em !important;
}

/* PAGINATION */
#search-pagination-wrapper-2 {
    background: #fff;
    border-top: 0;
    border-bottom: 1px solid #D6DCE2;
    float: none;
    padding-left: 10px;
    height: 40px;
}

#search-pagination-wrapper-2 ul {
    float: none;
}

#search-pagination-wrapper-2 ul.pagination:before {
    content: 'Pages';
    float: left;
    font-weight: bold;
    padding-right: 10px;
}

#search-pagination-wrapper-2 ul.pagination li {
    float: none;
}

#search-pagination-wrapper-2 ul.pagination li .next {
    display: none;
}

#search-pagination-wrapper-2 ul.pagination li a {
    padding: 10px;
}
}
