css:
@-moz-document domain("fimfiction.net"), domain("poni.0au.de") {
a.character_icon:hover {text-decoration: none !important}
.avatar, .fb_iframe_widget, .fb-like-box, div.header img, .light img[src*="/images/custom_banners/"], .nav_bar .right>img, .story_image, .story .left, div.title img, .user_toolbar [href*="MarkAllFavsRead"] {display: none !important}
.blog_post_content img:hover, .chapter img:hover, .comment_data img:hover, .story img:hover {
   height: auto;
   max-height: 500px;
   max-width: 500px !important}
.blog_post_content img, .chapter img, .comment_data img, .story img {max-height: 100px}
.character_icon:hover, .story .story_category:hover, .related_stories div img:hover {opacity: 1}
.character_icon, .story .story_category, .related_stories div img {opacity: .45}
.featured_box, div.user_toolbar .button {
   background-color: rgba(128, 128, 128, .05) !important;
   border: 1px solid rgba(37, 37, 37, .2) !important}
.featured_box {
   border-radius: 5px;
   height: 200px !important;
   left: 46% !important;
   margin-top: 10px !important;
   padding-bottom: 5px !important;
   top: -270px !important}
.featured_box .author {color: #555 !important}
.featured_box .description {color: #777 !important}
.featured_box .selection {padding-bottom: 5px}
.featured_box .selection a:hover {text-decoration: none !important}
.featured_box .selection a:after, .featured_box .selection .selected:after {content: "â€¢"}
.featured_box .selection a:after {color: #666}
.featured_box .selection .selected:after {color: #307FA4}
.featured_box .title a {
   background-color: transparent !important;
   color: #BDBDBD !important}
.featured_story_container .description {max-height: 110px !important}
#follower_list img:hover, .user_page .avatar img:hover, #watching_list img:hover {opacity: 1}
#follower_list img, #watching_list img {opacity: .75}
.gallery_story {height: auto !important}
.gallery_story .info {position: static !important}
.front_page {margin-bottom: 15px !important}
div.header, div.header div.title {height: 0 !important}
.header, .navbar, .user_toolbar {border-width: 0 !important}
div.header, div.title, .featured_box .selection a, .featured_box_overlay_left, .featured_box_overlay_right, .nav_bar .light {background-image: none !important}
img[src*="/emoticons/"]:hover, img[src*="http://dl.dropbox.com/u/31471793/"]:hover {opacity: 1 !important}
img[src*="/emoticons/"], img[src*="http://dl.dropbox.com/u/31471793/"] {opacity: .7}
img[src*="/images/icons/"]:hover {height: auto !important}
img[src*="/images/icons/"] {height: auto !important}
.inner div[style$="61px;"] div[style^="flo"]:hover {opacity: 1}
.inner div[style$="61px;"] div[style^="flo"] {opacity: .75}
.menu_list .button {min-width: 165px !important}
.nav_bar {
   background-image: -moz-linear-gradient(bottom, #6E852A 0, #1C1C1B 5%, #2F2F2E 100%) !important;
   background-image: -o-linear-gradient(bottom, #6E852A 0, #1C1C1B 5%, #2F2F2E 100%) !important;
   height: 324px !important}
.read_it_later_selected {
   background-color: rgba(248, 227, 124, .35);
   box-shadow: 0 0 30px #F8E37C}
.reading_story {padding: 0 !important}
.reading_story a {
   display: block;
   padding: 7px !important}
.story .right {margin-left: 15px !important}
.title a[href="/"]:hover {
   border-color: rgba(186, 226, 226, .1) !important;
   border-style: solid !important;
   border-right-width: 1px !important}
.title a[href="/"]:hover:after {
   color: #d9d9d9;
   content: "Home";
   float: center;
   font-size: 22px;
   text-shadow: 0 0 0.2em #0573c2, 0 0 0.2em #d9edec, 0 0 0.1em #4a4a4a}
.title a[href="/"] {
   height: 202px !important;
   left: 0 !important;
   margin-left: 0 !important;
   padding: 106px 0 0 6px !important;
   text-decoration: none !important;
   top: -324px !important;
   width: 70px !important}
.user_drop_down_menu .menu_list {
   background-image: -moz-linear-gradient(bottom, #1C1C1B 95%, #2F2F2E 100%) !important;
   background-image: -o-linear-gradient(bottom, #1C1C1B 95%, #2F2F2E 100%) !important;
   border-bottom: 1px solid #6E852A !important;
   border: none !important}
.user_page .avatar img {opacity: .3}
.user_page .bio .avatar {display:block !important}
div.user_toolbar {
   background-color: transparent !important;
   margin-top: -48px !important}
.user_toolbar .button {
   border-bottom: 1px solid rgba(110, 133, 42, .2) !important;
   color: #777 !important;
   text-shadow: none !important}
.user_toolbar .button:hover {
   background-color: rgba(28, 28, 27, .1) !important;
   border: 1px solid rgba(110, 133, 42, .5) !important;
   color: #DDD !important;
   text-shadow: none !important}
.user_toolbar .button input {vertical-align: -3px}
}
