@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(mail.yahoo.com) {

div#yUnivHead,
div#custom-folders-nav,
div#list-view,
h2#nav-applications,
span#main-btn-new,
span#btn-new,
ul#tablist,
#northbanner,
#mg_footer,
#MNW,
#MON,
#REC,
#ch_col_h1,
#ch_vhci,
#ycw,
#yuhead-promo,
.ycw-events {
   display: none !important;}

div.nav-bar,
div#main {
   top: 0px!important;}

div#shellnavigation,
div#shellcontent {
   top: 35px!important;}

#navigation {
   float: right !important;
   max-width: 185px !important;
   margin-left: -1px !important;
   margin-bottom: -0.5px !important;}

#pageContent {
   margin-left: 180px !important;
   overflow: auto !important;}

#gx_userprofile {
   margin-right: 180px !important;
   margin-top: -10px !important;}

#gx_vnews {
   margin-right: 180px !important;
   margin-top: 7px !important;
   margin-bottom: -13px !important;}

#gx_news {
   margin-right: 10px !important;}

.plainMail {
   margin-right: 10px !important;
   margin-top: -5px !important;
   margin-bottom: -5px !important;
   overflow: auto !important;}

#contentnavtop > DIV,
#contentnavbottom > DIV:last-child,
#showMessagePage > DIV:last-child > DIV:last-child {
   margin-right: 5px !important;}

.btn {
   border-radius: 2px !important;
   -moz-border-radius: 2px !important;}



/* Switched button positions 'Mail Searchbar' with 'Check/New Mail' & 'Print Mail' with 'Previous/Next Mail' */

#contentbuttonbartop,
#contentbuttonbarbottom,
#globalbuttonbartop,
#globalbuttonbarbottom {
   min-height: 23px !important;}

#globalbuttonbartop > DIV:first-child > FORM:last-child > P > SPAN,
#globalbuttonbarbottom > DIV:first-child > FORM:last-child > P > SPAN {
   position: absolute !important;
   right: 8px !important;}

#globalbuttonbartop > DIV:first-child > FORM:first-child > P > SPAN,
#globalbuttonbarbottom > DIV:first-child > FORM:first-child > P > SPAN {
   position: absolute !important;
   right: 69px !important;}

#searchtestfieldstop,
#searchtestfieldsbottom {
   position: absolute !important;
   left: 15px  !important;}

#showMessagePage > DIV:first-child {
   float: right !important;
   margin-right: 7px !important;}

#showMessagePage > DIV:nth-child(2) {
   float: left !important;
   margin-left: 0px !important;}



/* Moves the Mail Inbox 'Delete' button closer to the 'Yahoo Navigation Sidebar', when reading emails */

#contentbuttonbartop > FORM:first-child > SPAN,
#contentbuttonbarbottom > FORM:first-child > SPAN {
   position: absolute !important;
   right: 185px !important;}

.switchcgcontainer {
   display: none !important;}



/* To use a style section below move the symbol (star|forward-slash) at the bottom of a style section below and place it to the right of the style's section description */

