css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vampirefreaks.com") {


.maintop td[align] img{
display: none !important;
}

.mainheading td[width], .side_td[align=right]{
display: none !important;
}

.maintop td[align]{
height: 45px;
background-color: transparent!important;
background-image: url('http://i26.tinypic.com/2llzpzq.gif');
background-attachment: scroll;
background-repeat: no-repeat;
background-position: center center;
}

body, html{
background-color: skyblue !important;
color: white !important;
font: 11px verdana !important;
}

table{ 
font: 11px verdana !important; 
}

a:link, a:hover, a:visited, a:active {
font: 11px verdana !important;
font-weight: bold !important;
color: #ffffff!important;
background-color: transparent!important;
}

input, select, textarea{
font: 11px verdana!important;
color: #54ae51!important;
background-color: #ffffff !important;
border:1px solid #999999 !important; 
border-style: solid !important; 
border-width: 1px !important; 
}

input[type="submit"]{
color: #000000!important;
}

.leftnav, .topbar, .comments{
border-width:1px;
border-style: solid;
border-color: #7dc27b !important;
}

.leftnav {
background-color: #54ae51;
text-align: center;
}

table[bgcolor="#151515"]{
border: 1px solid #7dc27b !important;
background-color: #54ae51 !important; 
}

table[bgcolor="#151515"] a{
color: #ffffff!important;
}

.darkbox, .darkbox td{
border: 1px solid #7dc27b !important;
background-color: #54ae51 !important; 
}

.darkbox a{
color: #ffffff!important;
}

.tdbordered{
border: none!important;
}

.notify{
color: #FFFFFF !important;
}

.leftnav a, .mainheading a {
display: block !important;
font-weight: normal !important;
color: #040404 !important;
}

.leftnav a:hover, .mainheading a:hover {
background: #7dc27b !important;
color:#FFFFFF !important;
font-weight: normal !important;
}

.menu, a.menu, a.menu:visited{
background-color: #c9eeff !important;
color: black !important;
font: 12px Verdana !important;
font-weight: normal !important;
text-decoration: none !important;
}

.menu2, a.menu2, a.menu2:visited{ 
background-color: #66bee6 !important;
color: #040404 !important;
font: 12px Verdana !important;
font-weight: normal !important;
text-decoration: none !important;
}

.heading{
background-image: url('http://i27.tinypic.com/25z4avs.gif') !important;
background-repeat: repeat-x !important;
background-color: #54ae51 !important;
}

.mainupdates .main_td{
background-color: #54ae51 !important;
}

.side_td[align="right"]{
display: none;
}

tr[bgcolor="#222222"], .darkrow{
background-color: #7FBFFF!important;
}

tr[bgcolor="#000000"], .lightrow{
background-color: transparent!important;
}

.darkrow>td[width="20"], 
.lightrow>td[width="20"]{
width: 20px!important;
height: 20px!important;
overflow: hidden!important;
}

.darkrow>td[width="20"] img, 
.lightrow>td[width="20"] img{
background-image: url('http://i27.tinypic.com/14n1bif.gif');
background-attachment: scroll;
background-repeat: no-repeat;
background-position: center center;
padding-left: 50px;
margin-right: -27px;
overflow: hidden;
}

.forum_comment td{
background-image: url('http://i27.tinypic.com/25z4avs.gif') !important;
background-repeat: repeat-x !important;
background-color: #54ae51 !important;
}
}
