css:
@-moz-document domain("www.guokr.com") {

/*

* UserStyles.org: Read Guokr  (Style 62730)
*
* Author      : tumuyan
* Created     : Mar 18, 2012
* Updated     : Mar 19, 2012
* Version     : 0.3.0 - release
* Description : none
* Usage       : For use with Stylish, a Firefox/Chrome extension (http://userstyles.org/)
*

*/



}


/*
·····························
·
·主题站部分
·
·····························
*/
@-moz-document url-prefix("http://www.guokr.com/article") {


.side{

position:fixed !important;
right:6% !important;top:90px !important;
background:#fff !important;
border-radius:15px!important;
border:1px solid #ccc!important;
padding:10px!important;
height:125px!important;


}

.side:hover{
height:540px!important;
   -moz-transition:  0.5s ease-in-out;
   -webkit-transition: all  .5s ease-in-out;
   -o-transition:   .5s ease-in-out;
}



#commentsReplyer{width:315px !important;}
#commentsReplyer{
position:fixed;
right:6% !important;bottom:60px !important;
overflow:hidden;
}

#commentsReplyer:hover{height:auto !important;}
#editorBorder1{height:20px}
#editorBorder1:hover{height:100px;
   -moz-transition:  0.5s ease-in-out;
   -webkit-transition: all  .5s ease-in-out;
   -o-transition:   .5s ease-in-out;
}
.gui-ubb-b,.gui-ubb-links{background:#fcfcfc !important;}

p.cmts-do-hd,
#commentsReplyer img.fl,
a.gui-ubb-ul,
.side-title{display:none !important;}



@media all and (min-width:1000px) and (min-height:400px) {

.content-related{
background:#fff !important;
position:fixed !important;
right:6% !important;top:230px !important;width:290px !important;
border-radius:15px !important;
border:1px solid #ccc !important;
padding:10px 10px !important;

}

.content-related li {
float: right !important;
margin-right: 10px !important;
margin-left: 10px !important;

}

.content-related li a {
width:122px!important;
color:#ccc !important;
}
.content-related li a:hover{
color:#069 !important;
}

.content-related ul li img{
border:1px solid #999;
}

}

@media all and  (max-height:700px){
.side-img{display:none !important;}
#commentsReplyer{bottom:0px !important;}
}


@media all and  (max-width:1000px){
.side,#commentsReplyer{right:3% !important;}

}

@media all and  (max-width:900px){
.side,#commentsReplyer{right:10px !important;}

}
@media all and  (max-width:850px){
.side,#commentsReplyer{right:0px !important;}

}
@media all and  (max-width:800px){
.side{display:none !important;}
#commentsReplyer{bottom:0px !important;}
}

/*
.container,top-wp,.wrap article-page{background:#f6f6f6 !important} 
.content-txt{background:#fafafa !important;
width: 750px !important;
padding:35px 50px !important;

} 
width:100px;
height:160px;
a.gui-ubb-bold,a.gui-ubb-italic,

.main-title-b li{
border-radius:15px;
border:5px solid #fff;
border-radius:10px;
}

*/
}



/*
·····························
·
·主页部分
·
·····························
*/


@-moz-document url("http://www.guokr.com/"), regexp("http://www\\.guokr\.com/\?page=\d") {
@media all and (min-width:950px) and (min-height:500px) {
.side{
position:relative;
top:-30px;

}

a.side-img
{height:5px !important;
overflow:hidden !important;
}

a.side-img:hover{
overflow:visible !important;
height:160px !important;
   -moz-transition:  0.5s ease-in;
   -webkit-transition: all  .5s ease-in;
   -o-transition:   .5s ease-in;
}

.pack{
height:100px !important;

background:#fff !important;
border-radius:10px!important;
border:1px solid #ccc!important;
padding:0px!important;



}
.pack:hover{
height:198px !important;
   -moz-transition:  0.5s ease-out;
   -webkit-transition: all  .5s ease-out;
   -o-transition:   .5s ease-out;
}


.nolist{display:none !important;}

.side-reco{position:fixed !important;
bottom:50px !important;
background:#fff !important;
border-radius:10px 0px 0px 10px !important;
border:1px solid #ccc!important;
padding:20px 10px !important;

box-shadow:-3px -2px 5px #ddd !important;
}

.side-reco h2{
position:absolute;
right:20px;
}


p.side-reco-more{

position:absolute;
right:20px;
top:45px;
}

#sideNotification{
position:fixed !important;
float:left !important;
bottom:35px !important;
right:0px !important;
z-index:10 !important;
}
ul#notificationList{


}

}
}


/*
·····························
·
·小组发言
·
·····························
*/


@-moz-document url-prefix("http://www.guokr.com/post/") {
@media all and (min-width:800px) and (min-height:450px) {
.side{
position:relative !important;
top:-30px !important;

}
.site-relative{
position:fixed;
bottom:30px !important;
background:#fcfcfc !important;
border:1px solid #d0d0d0 !important;
padding:5px 20px !important;
border-radius:25px !important;
right:70px !important;
box-shadow: 9px 5px 9px #ccc !important;
z-index:15 !important;
}
.site-relative:hover{

background:#f6f6f6 !important;
box-shadow: 9px 5px 9px #bbb !important;
   -moz-transition:  0.5s ease-in;
   -webkit-transition: all  .5s ease-in;
   -o-transition:   .5s ease-in;
}


.side div:first-child{
display:none;

}

.side .pack~.side-title
{
position:fixed;
bottom:60px !important;
 padding-left:10px;
z-index:13 !important;
}

.side .side-title~.titles {
background:#fff;
position:fixed;
bottom:65px !important;
overflow:hidden !important;
border:1px solid #d0d0d0;
padding:25px 5px !important;
border-radius:5px;
box-shadow: 3px 5px 4px #ccc;
}

.side .side-title~.titles:hover{

background:#fcfcfc !important;
box-shadow: 3px 5px 4px #bbb !important;
   -moz-transition:  0.5s ease-in;
   -webkit-transition: all  .5s ease-in;
   -o-transition:   .5s ease-in;
}


a.side-img
{height:5px !important;
overflow:hidden !important;
}

a.side-img:hover{
overflow:visible !important;
height:160px !important;
   -moz-transition:  0.5s ease-in;
   -webkit-transition: all  .5s ease-in;
   -o-transition:   .5s ease-in;
}
}
}




/*

focus 单页规则——一切尽在一页当中
*/



@-moz-document url("http://www.guokr.com/focus/") {




.nuts{position:absolute !important;top:380px !important;
background:#8ac73f !important;
width:350px !important;
height:0px !important;
padding: 10px 0px !important;
overflow:hidden  !important;
z-index:5 !important; 
}

.nuts:hover{
background:transparent !important;
width:630px !important;
height:250px !important;
padding: 10px 10px !important;
-o-transition: .5s ease-in-out;
-webkit-transition: .5s ease-in-out;
-moz-transition: .5s ease-in-out;
}

.groups{position:absolute !important;top:365px !important;margin-left:320px !important;
z-index:5 !important; 
background:#25ABDD !important;
width:300px !important;
height:0px !important;
padding: 10px 5px !important;
overflow:hidden  !important;}
.groups:hover{margin-left:0px !important;
background:#eee !important;
width:640px !important;
height:300px !important;
border:1px solid #eff;
border-radius:13px;
padding: 10px 0px !important;
background:transparent !important;
-o-transition:height .5s ease-in;
-moz-transition:height .5s ease-in;
-webkit-transition:height .5s ease-in;
}


ul.groups li{
border-radius:10px;
background:#ffffec !important;
padding:3px 8px !important;
margin:2px 2px 0px 0px !important;
box-shadow:-3px 2px 6px #eee;
border:1px solid #ccc
}
ul.groups li:hover{
border-radius:10px;
background:#fffff8 !important;
padding:3px 8px !important;
box-shadow:-4px 3px 8px #ccc;
}

ul.groups li a:hover{
color:#f00;
box-shadow:-4px 3px 4px #999;
} 

/*
.recos + .main-title-b .main-title{display:none !important;}

.nuts + .main-title-b .main-title{display:none !important;}
*/

.nuts + .main-title-b ,.recos + .main-title-b{display:none !important;}

 .main-title-b  .main-title{display:inline !important;}
.nuts + .main-title-b  .main-title,.recos + .main-title-b .main-title
{
position:fixed !important;top:365px !important;left:100px !important;
}



.side-img,.bottom,
.side ul.side-event + .side-title
{display:none !important;}



.recos:hover{position:fixed;bottom:12px !important;width:660px !important;background:#ffe !important;box-shadow:-8px -5px 12px #ccc;padding:15px !important;
z-index:15 !important; 
border:1px solid #eee !important;
border-radius:13px;
-o-transition: .5s ease-in;
-moz-transition: .5s ease-in;
-webkit-transition: .5s ease-in;
}




ul.nuts-r li{

border:1px solid #eee;
border-radius:2px;
background:#fcfcfc !important;box-shadow:-5px 5px 16px #aaa;
}

ul.nuts-r li:hover{

border:1px solid #ccc;
border-radius:2px;
background:#fff !important;box-shadow:-6px 5px 17px #bbb;
}

ul.nuts-r {position:absolute !important;left:25px !important;top:20px !important;
z-index:6 !important;
}


.nuts-l{
padding:10px !important;
position:absolute !important;left:320px !important;
border:1px solid #eee;
border-radius:13px;
background:#ffe !important;box-shadow:-5px 3px 4px #ccc;
z-index:10 !important;
}

}
