css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {

/******BETTER STYLES (white text etc)*****************************************************************************/
.st-bg-today, .st-bg-td-first, .st-bg-td-last, .dp-today-selected { background-color: #E7EEF8 !important; }
.tg-today { background-color: #FFFBCF !important; }
.rb-n { color: #FFFFFF !important; }
.chip { color: #FFFFFF !important; }
.rszr-icon-hover { border-color: #FFFFFF !important; }

/*** classic background colors ***/
.wk-weektop {background-color: #C5DBFD !important;}
.wk-allday, .tg-times-pri, .tg-times-sec {background-color: #E8EEF6 !important;}
.dp-weekend, .dp-weekendh {background-color: #EDEDED !important;}
.wk-dayname {color: #0000AE !important;}
.wk-today {background: none repeat scroll 0 0 #88AACD !important;}
.dp-weekday-selected, .dp-weekend-selected {background-color: #ACCBE7 !important;}
.dp-today-selected {background-color: #517992 !important;}
.dp-sb-cur {color: #0138C6 !important;}
.dp-weekday-selected, .dp-weekend-selected, .wk-today {color: #000000 !important;}
.evt-lk { color: #FFFFFF !important; }
.rb-n {background-color: #668BD9 !important;}
.rb-n:hover {background-color: #2F509F !important;}
.dp-today-selected { color: #FFFFFF !important; }0
.dp-today { background: none repeat scroll 0 0 #FFFBCF !important; }
.dp-today { border-color: #FAD68E !important; }
.rb-ro-10 { background-image: none !important;}
.lv-event-title { color: #668BD9 !important; }
.te { color: #668BD9 !important; }
.ui-sch { color: #668BD9 !important; }

/*** white symbols ***/
.cic-tmr, .cic-tmr-inv {
    background-position: -11px -8px!important;
    height: 10px!important;
    width: 11px!important; }
    
.cic-rcr, .cic-rcr-inv {
    background-position: -42px -8px!important;
    height: 9px!important;
    line-height: 7px!important;
    width: 9px!important; }

.cic-spcl, .cic-spcl-inv {
    background-position: -52px -8px!important;
    height: 8px!important;
    line-height: 7px!important;
    width: 9px!important; }

/*** white text for individual calendar items ***/
dl.cbrd dd span {color:#fff !important;}
dl.cbrd dt {color:#fff !important;}
dl.cbrd:hover {background-color:#2F509F !important; }
dl.cbrd {background-color: #668BD9 !important;}
dl.cbrd dt {background-color: #2F509F !important;} }
