css:
@-moz-document domain('www.mediumdifficulty.com'), url-prefix('http://www.mediumdifficulty.com/') {
 body {background:#F0F0F0;}
 #page {padding:10px 0; width:90%; min-width:900px;}
 #header {height:50px; position:relative;}
 #sitename {font-size:2.6em; float:none; position:absolute; display:block; width: 270px; left:25px; bottom:3px;}
 #nav {line-height:2em; font-size:1.1em; padding-top:0; width:40%; bottom:4px; left:300px; float:none; position:absolute; display:block;}
 #nav li{margin-right:2em;}
 #searchform {padding-top:0; bottom:3px; right:0; float:none; position:absolute; display:block;}
 #content {width:57%; margin:0 3% 0 10%;}
 .entry {font-size:1.3em; line-height:1.5em;}
 #sidebar {line-height:1.3em; width:22%; margin:0 5% 0 3%;}
 #sidebar ul li {text-align:right;}
 #sidebar ul ul {list-style:none; margin-left:0;}
 #sidebar li li {margin:0; padding-left:0;}
 #sidebar cite:hover {text-decoration:underline;}
 p {margin: 1.5em 0;}
 a {text-decoration:underline;}
 a.bubble {background:#DFDFDF; border-radius:9px; line-height:29px; color:#888888;}
 .bubble span {text-decoration:none; font-weight:bold;}
}
