css:
body {
 background: #efefef !important; color: #aaa !important; }

img[src*="/banner/"], img.banner {
 display: none !important; }
 
 h1, form, td, .postbody, .newsbody, .site_name {
 color: #605b5b !important; }

.file_thread {
 background: #fffff  !important;
}

.menu_title, h2 {
 color: #aaa !important;
}

.menu_title, h2 {
 background: #e4e3e3 !important; }

 .postreply, .newsbody, .postreply_highlighted, td.label, span.hidden_thread, .board_newposts, .postreply {
 background: #e4e3e3 !important; }

.file_thread, .file_reply {
 background: #e4e3e3 !important;
 margin: 5px 10px 20px 5px !important; padding: 5px !important; }

.file_thread {
 border: 1px solid #e4e3e3 !important; }

.file_reply {
 border: 1px solid #e4e3e3 !important; }

.postreply, .newsbody, table, tr, td, td.label, span.hidden_thread {
 border: none !important; }

.postreply_highlighted {
 border: 2px dashed #eeaa88 !important; }

span.quote {
 color: #789922 !important; font-style: normal; font-size: 90%; }
 
 .spoiler {
 color: #000 !important; background: #000000 !important; }

span.spoiler:hover {
 color: #ffffff  !important; }

.authority_admin, .ban_mark, .sage {
color: #c53838  !important;
}

span.nav_group_button {
 background: #484848 !important;
 border-color: #aaa !important;
 color: #aaa !important; }

.postheader .postdate, .site_slogan {
 color: #666 !important; }

.postreply .postdate, .postreply_highlighted .postdate, .postheader a.quotelink {
 color: #3b3b3b !important;
}
.postername {
 color: #184565 !important;
}

.postform ul {
 font-size: 90% !important; }

a {
 color: #0067be !important; }

.boardlist a {
 font-size: 90% !important;
 color: #789922 !important;
}

.boardlist a:hover {
 background: #222 !important;
}

}
#postform:hover{
right: 0px !important;
width: 50%;
} 


